// src/components/AuthoritySidebar.js
import React from "react";
import { Link, useLocation } from "react-router-dom";
import { FaTachometerAlt, FaUserGraduate, FaSignOutAlt } from "react-icons/fa";

const AuthoritySidebar = () => {
  const location = useLocation();

  const navItems = [
    {
      name: "Dashboard",
      icon: <FaTachometerAlt />,
      path: "/authority/dashboard",
    },
    {
      name: "Manage Users",
      icon: <FaUserGraduate />,
      path: "/authority/users",
    },
    {
      name: "Logout",
      icon: <FaSignOutAlt />,
      path: "/authority/logout",
    },
  ];

  return (
    <div className="h-full bg-white dark:bg-gray-900 shadow-md w-64 px-4 py-6">
      <h2 className="text-xl font-bold text-blue-700 mb-6 text-center">
        ðŸŽ“ Authority Panel
      </h2>
      <nav className="space-y-2">
        {navItems.map((item, index) => {
          const isActive = location.pathname === item.path;
          return (
            <Link
              key={index}
              to={item.path}
              className={`flex items-center gap-3 px-4 py-2 rounded-lg transition-all ${
                isActive
                  ? "bg-blue-100 text-blue-700 font-semibold dark:bg-blue-800 dark:text-white"
                  : "text-gray-700 hover:bg-gray-100 dark:text-gray-300 dark:hover:bg-gray-800"
              }`}
            >
              {item.icon}
              <span>{item.name}</span>
            </Link>
          );
        })}
      </nav>
    </div>
  );
};

export default AuthoritySidebar;
