// src/admin-pages/AdminTrusted.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTrash, FaUserShield, FaPlusCircle, FaUserCheck } from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE || 'http://localhost:5000/api',
});

const AdminTrusted = () => {
  const [trustedPersons, setTrustedPersons] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    role: '',
    image: null,
  });
  const [preview, setPreview] = useState(null);

  const fetchTrusted = async () => {
    try {
      const res = await API.get('/trusted');
      setTrustedPersons(res.data);
    } catch (err) {
      console.error('Error fetching trusted persons:', err);
    }
  };

  useEffect(() => {
    fetchTrusted();
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'image') {
      const file = files?.[0];
      setFormData((prev) => ({ ...prev, image: file || null }));
      setPreview(file ? URL.createObjectURL(file) : null);
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.image) return alert('Please select an image.');

    const data = new FormData();
    data.append('type', 'trusted');
    Object.entries(formData).forEach(([key, value]) => data.append(key, value));

    try {
      await API.post('/trusted', data);
      fetchTrusted();
      setFormData({ name: '', email: '', role: '', image: null });
      setPreview(null);
      alert('Trusted person added!');
    } catch (err) {
      console.error('Upload failed:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await API.delete(`/trusted/${id}`);
      fetchTrusted();
    } catch (err) {
      console.error('Delete failed:', err);
    }
  };

  const buildImageUrl = (imagePath) =>
    `${process.env.REACT_APP_API_BASE}/${imagePath.replace(/\\/g, '/')}`;

  return (
    <div className="p-6 space-y-8">
      <h1 className="text-2xl font-bold flex items-center gap-2 text-blue-700">
        <FaUserShield /> Manage Trusted Persons
      </h1>

      <form onSubmit={handleSubmit} encType="multipart/form-data" className="grid gap-4 grid-cols-1 md:grid-cols-2 lg:grid-cols-3 bg-white p-4 shadow rounded-lg">
        <input type="text" name="name" placeholder="Name" value={formData.name} onChange={handleChange} required className="border p-2 rounded" />
        <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required className="border p-2 rounded" />
        <input type="text" name="role" placeholder="Role / Note" value={formData.role} onChange={handleChange} className="border p-2 rounded" />
        <input type="file" name="image" accept="image/*" onChange={handleChange} required className="border p-2 rounded" />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded flex items-center justify-center">
          <FaPlusCircle className="mr-2" /> Add
        </button>
        {preview && <img src={preview} alt="Preview" className="w-24 h-24 object-cover border rounded-lg" />}
      </form>

      <div>
        <h2 className="text-lg font-semibold text-green-700 flex items-center gap-2 mb-3">
          <FaUserCheck /> Trusted Persons
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {trustedPersons.map((person) => (
            <div key={person._id} className="bg-white p-4 rounded-lg shadow relative">
              <img src={buildImageUrl(person.image)} alt={person.name} className="w-full h-40 object-cover rounded" />
              <div className="mt-2">
                <h3 className="font-bold">{person.name}</h3>
                <p>{person.email}</p>
                <p className="text-sm text-gray-600">{person.role}</p>
              </div>
              <button onClick={() => handleDelete(person._id)} className="absolute top-2 right-2 text-red-500 hover:text-red-700">
                <FaTrash />
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminTrusted;
