import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  FaTrash,
  FaUser,
  FaEnvelope,
  FaRupeeSign,
  FaBook,
  FaArrowLeft,
  FaArrowRight,
} from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const Book = () => {
  const [books, setBooks] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const booksPerPage = 6;

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await API.get('/api/books');
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const indexOfLastBook = currentPage * booksPerPage;
  const indexOfFirstBook = indexOfLastBook - booksPerPage;
  const currentBooks = books.slice(indexOfFirstBook, indexOfLastBook);
  const totalPages = Math.ceil(books.length / booksPerPage);

  return (
    <div className="p-6 min-h-screen bg-gray-100">
      <h2 className="text-3xl font-bold text-gray-800 mb-6 flex items-center gap-2">
        <FaBook className="text-blue-600" /> Available Books
      </h2>

      {books.length === 0 ? (
        <p className="text-gray-600">No books available.</p>
      ) : (
        <>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {currentBooks.map((book) => (
              <div
                key={book._id}
                className="bg-white rounded-2xl shadow-md hover:shadow-lg transition duration-300 p-5 flex flex-col"
              >
                <img
                  src={`${process.env.REACT_APP_API_BASE}${book.image}`}
                  alt={book.name}
                  className="w-full h-48 object-cover rounded-lg mb-4"
                />
                <div className="mb-1">
                  <span className="font-semibold text-gray-700">Book Name:</span>
                  <span className="ml-2 text-gray-800">{book.name}</span>
                </div>
                <div className="mb-1 flex items-center gap-2">
                  <FaUser className="text-blue-500" />
                  <span className="font-semibold text-gray-700">Seller:</span>
                  <span className="text-gray-800">{book.seller}</span>
                </div>
                <div className="mb-1 flex items-center gap-2">
                  <FaEnvelope className="text-green-500" />
                  <span className="font-semibold text-gray-700">Email:</span>
                  <span className="text-gray-800">{book.email}</span>
                </div>
                <div className="mt-2 flex items-center gap-2 text-lg">
                  <FaRupeeSign className="text-yellow-500" />
                  <span className="font-semibold text-gray-700">Price:</span>
                  <span className="text-gray-800 font-bold">{book.price}</span>
                </div>
              </div>
            ))}
          </div>

          {/* Pagination */}
          <div className="flex justify-center items-center mt-10 gap-6">
            <button
              onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
              disabled={currentPage === 1}
              className={`flex items-center gap-2 px-4 py-2 rounded-lg transition ${
                currentPage === 1
                  ? 'bg-gray-300 cursor-not-allowed text-gray-600'
                  : 'bg-blue-500 text-white hover:bg-blue-600'
              }`}
            >
              <FaArrowLeft /> Prev
            </button>
            <span className="text-gray-800 font-medium">
              Page {currentPage} of {totalPages}
            </span>
            <button
              onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
              disabled={currentPage === totalPages}
              className={`flex items-center gap-2 px-4 py-2 rounded-lg transition ${
                currentPage === totalPages
                  ? 'bg-gray-300 cursor-not-allowed text-gray-600'
                  : 'bg-blue-500 text-white hover:bg-blue-600'
              }`}
            >
              Next <FaArrowRight />
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default Book;
