import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faBars,
  faTimes,
  faUniversity,
} from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';

export default function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const toggleMenu = () => setIsOpen(!isOpen);
  const closeMenu = () => setIsOpen(false);

  return (
    <nav className="bg-blue-600 text-white p-4 shadow-md">
      <div className="flex flex-col sm:flex-row justify-between items-center max-w-7xl mx-auto">

        {/* Top row for small screens */}
        <div className="flex justify-between items-center w-full sm:hidden mb-2">
          <button
            onClick={toggleMenu}
            className="text-white text-2xl"
          >
            <FontAwesomeIcon icon={isOpen ? faTimes : faBars} />
          </button>
          <h1 className="text-xl font-bold flex items-center">
            <FontAwesomeIcon icon={faUniversity} className="mr-2" />
            ARMS
          </h1>
        </div>

        {/* Menu (Left side on desktop, top on mobile when open) */}
        <ul className={`flex flex-col sm:flex-row gap-4 text-sm sm:text-base ${isOpen ? 'block' : 'hidden'} sm:flex`}>
          <li><Link to="/" onClick={closeMenu} className="hover:underline block py-1">Home</Link></li>
          <li><Link to="/departments" onClick={closeMenu} className="hover:underline block py-1">Departments</Link></li>
          <li><Link to="/university" onClick={closeMenu} className="hover:underline block py-1">University</Link></li>
          <li><Link to="/contact" onClick={closeMenu} className="hover:underline block py-1">Contact Us</Link></li>
          <li><Link to="/members" onClick={closeMenu} className="hover:underline block py-1">Special Members</Link></li>
          <li><Link to="/admin" onClick={closeMenu} className="hover:underline block py-1">Admin Login</Link></li>
        </ul>

        {/* Heading (Right side on desktop) */}
        <h1 className="hidden sm:flex text-xl font-bold items-center">
          <FontAwesomeIcon icon={faUniversity} className="mr-2" />
          Academic Resource Management System
        </h1>
      </div>
    </nav>
  );
}
