import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import {
  FaBars,
  FaTimes,
  FaTachometerAlt,
  FaBookOpen,
  FaUniversity,
  FaUser,
  FaSignOutAlt,
} from 'react-icons/fa';

const UserSidebar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const toggleSidebar = () => setIsOpen(!isOpen);

  // Prevent background scroll when sidebar is open on mobile
  useEffect(() => {
    if (isOpen) {
      document.body.classList.add('overflow-hidden');
    } else {
      document.body.classList.remove('overflow-hidden');
    }
    return () => document.body.classList.remove('overflow-hidden');
  }, [isOpen]);

  const baseLink =
    'flex items-center gap-3 px-4 py-2.5 rounded-lg transition-all hover:scale-[1.02]';

  const activeLink =
    'bg-gradient-to-r from-blue-600 to-blue-500 text-white font-semibold shadow-sm';

  const sectionTitle = (title) => (
    <div className="flex items-center gap-2 mt-6 mb-3 px-4 py-1.5 text-xs font-semibold uppercase tracking-wider text-gray-600 rounded-md bg-gray-100">
      <span className="w-2 h-2 rounded-full bg-blue-500 animate-pulse"></span>
      {title}
    </div>
  );

  return (
    <>
       {/* Mobile Topbar */}
           <div className="md:hidden fixed top-0 left-0 right-0 bg-white dark:bg-gray-900 shadow-md z-50 flex items-center justify-between px-4 h-14">
       {/* Left: Sidebar Toggle Button */}
       <div className="w-10 flex justify-start">
         <button
  onClick={toggleSidebar}
  className="text-gray-800 dark:text-white focus:outline-none"
>
  {isOpen ? <FaTimes size={22} /> : <FaBars size={22} />}
</button>

       </div>
     
       {/* Center: Title and Subtitle */}
       <div className="absolute left-1/2 transform -translate-x-1/2 text-center">
         <h1 className="text-lg font-extrabold text-blue-700 dark:text-white">
           ðŸŽ“ Academic Source
         </h1>
         <p className="text-xs text-gray-500 dark:text-gray-400">
           Admin Dashboard
         </p>
       </div>
     
       {/* Right: Empty space to balance left icon width */}
       <div className="w-10" />
     </div>
     

      {/* Sidebar */}
      <aside
        className={`${isOpen ? 'translate-x-0' : '-translate-x-full'
          } lg:translate-x-0 fixed lg:static top-0 left-0 w-64 h-full bg-white border-r border-gray-200 shadow-lg transition-transform duration-300 z-40 flex flex-col`}
      >
        {/* Logo */}
        <div className="p-6 border-b border-gray-200 text-center">
          <h2 className="text-2xl font-extrabold text-blue-700">
            ðŸŽ“ Academic Source
          </h2>
          <p className="text-xs text-gray-500 mt-1">User Panel</p>
        </div>

        {/* Navigation */}
        <nav className="p-4 text-sm text-gray-800 flex flex-col flex-grow overflow-y-auto">
          {sectionTitle('Overview')}

          <NavLink
            to="/user"
            onClick={() => setIsOpen(false)}
            className={({ isActive }) =>
              `${baseLink} ${isActive ? activeLink : 'hover:bg-blue-50'}`
            }
          >
            <FaTachometerAlt />
            <span>Dashboard</span>
          </NavLink>

          <NavLink
            to="/user/profile"
            onClick={() => setIsOpen(false)}
            className={({ isActive }) =>
              `${baseLink} ${isActive ? activeLink : 'hover:bg-blue-50'}`
            }
          >
            <FaUser />
            <span>My Profile</span>
          </NavLink>

          <NavLink
            to="/user/resources"
            onClick={() => setIsOpen(false)}
            className={({ isActive }) =>
              `${baseLink} ${isActive ? activeLink : 'hover:bg-blue-50'}`
            }
          >
            <FaBookOpen />
            <span>My Resources</span>
          </NavLink>

          <NavLink
            to="/user/university"
            onClick={() => setIsOpen(false)}
            className={({ isActive }) =>
              `${baseLink} ${isActive ? activeLink : 'hover:bg-blue-50'}`
            }
          >
            <FaUniversity />
            <span>University Info</span>
          </NavLink>

          {/* Logout */}
          <div className="mt-auto pt-6 border-t border-gray-200">
            <NavLink
              to="/user/logout"
              onClick={() => setIsOpen(false)}
              className={({ isActive }) =>
                `${baseLink} ${isActive
                  ? activeLink
                  : 'hover:bg-red-100 text-red-600'}`
              }
            >
              <FaSignOutAlt />
              <span>Logout</span>
            </NavLink>
          </div>
        </nav>
      </aside>
    </>
  );
};

export default UserSidebar;
