import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import {
  FaBars,
  FaTimes,
  FaTachometerAlt,
  FaBookOpen,
  FaUniversity,
  FaUser,
} from 'react-icons/fa';

const UserSidebar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => setIsOpen(!isOpen);

  const baseLink =
    'flex items-center gap-3 px-4 py-2.5 rounded-lg transition-all hover:scale-[1.02]';

  const activeLink =
    'bg-gradient-to-r from-blue-600 to-blue-500 text-white font-semibold shadow-sm';

  const sectionTitle = (title, color = 'bg-gray-100 dark:bg-gray-800') => (
    <div
      className={`flex items-center gap-2 mt-6 mb-3 px-4 py-1.5 text-xs font-semibold uppercase tracking-wider text-gray-600 dark:text-gray-300 rounded-md ${color}`}
    >
      <span className="w-2 h-2 rounded-full bg-blue-500 animate-pulse"></span>
      {title}
    </div>
  );

  return (
    <>
      {/* Mobile Top Bar */}
      <div className="lg:hidden flex items-center justify-between p-4 bg-white dark:bg-gray-900 border-b border-gray-200 dark:border-gray-800 shadow-md z-50">
        <h2 className="text-xl font-bold text-blue-700 dark:text-white">
          ðŸŽ“ Academic Source
        </h2>
        <button
          onClick={toggleSidebar}
          className="text-2xl text-blue-700 dark:text-white focus:outline-none"
        >
          {isOpen ? <FaTimes /> : <FaBars />}
        </button>
      </div>

      {/* Sidebar */}
      <aside
        className={`${
          isOpen ? 'translate-x-0' : '-translate-x-full'
        } lg:translate-x-0 fixed lg:static top-0 left-0 w-64 h-screen bg-white dark:bg-gray-900 border-r border-gray-200 dark:border-gray-800 shadow-lg transition-transform duration-300 z-40`}
      >
        {/* Logo */}
        <div className="p-6 border-b border-gray-200 dark:border-gray-700 text-center">
          <h2 className="text-2xl font-extrabold text-blue-700 dark:text-white">
            ðŸŽ“ Academic Source
          </h2>
          <p className="text-xs text-gray-500 dark:text-gray-400 mt-1">User Panel</p>
        </div>

        {/* Navigation */}
        <nav className="p-4 text-sm text-gray-800 dark:text-gray-200">
          {sectionTitle('Overview', 'bg-blue-100 dark:bg-blue-950')}

          <NavLink
            to="/user"
            onClick={() => setIsOpen(false)}
            className={({ isActive }) =>
              `${baseLink} ${
                isActive ? activeLink : 'hover:bg-blue-50 dark:hover:bg-gray-700'
              }`
            }
          >
            <FaTachometerAlt />
            <span>Dashboard</span>
          </NavLink>

          <NavLink
            to="/user/profile"
            onClick={() => setIsOpen(false)}
            className={({ isActive }) =>
              `${baseLink} ${
                isActive ? activeLink : 'hover:bg-blue-50 dark:hover:bg-gray-700'
              }`
            }
          >
            <FaUser />
            <span>My Profile</span>
          </NavLink>

          <NavLink
            to="/user/resources"
            onClick={() => setIsOpen(false)}
            className={({ isActive }) =>
              `${baseLink} ${
                isActive ? activeLink : 'hover:bg-blue-50 dark:hover:bg-gray-700'
              }`
            }
          >
            <FaBookOpen />
            <span>My Resources</span>
          </NavLink>

          <NavLink
            to="/user/university"
            onClick={() => setIsOpen(false)}
            className={({ isActive }) =>
              `${baseLink} ${
                isActive ? activeLink : 'hover:bg-blue-50 dark:hover:bg-gray-700'
              }`
            }
          >
            <FaUniversity />
            <span>University Info</span>
          </NavLink>
        </nav>
      </aside>
    </>
  );
};

export default UserSidebar;
