import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaFilePdf, FaTrash, FaPlus, FaUpload } from 'react-icons/fa';

const Notes = () => {
  const [notes, setNotes] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newNote, setNewNote] = useState({
    university: '',
    course: '',
    semester: '',
    subject: '',
    pdf: null,
  });

  useEffect(() => {
    axios
      .get('http://localhost:5000/api/notes')
      .then((res) => setNotes(res.data))
      .catch((err) => console.error('Failed to fetch notes:', err));
  }, []);

  const handleFileChange = (e) => {
    setNewNote({ ...newNote, pdf: e.target.files[0] });
  };

  const handleChange = (e) => {
    setNewNote({ ...newNote, [e.target.name]: e.target.value });
  };

  const handleAdd = async () => {
    const { university, course, semester, subject, pdf } = newNote;
    if (!university || !course || !semester || !subject || !pdf) {
      return alert('Please fill all fields!');
    }

    const formData = new FormData();
    formData.append('university', university);
    formData.append('course', course);
    formData.append('semester', semester);
    formData.append('subject', subject);
    formData.append('pdf', pdf);

    try {
      const res = await axios.post('http://localhost:5000/api/notes', formData);
      setNotes([...notes, res.data.note]);
      setNewNote({ university: '', course: '', semester: '', subject: '', pdf: null });
      setShowForm(false);
    } catch (err) {
      console.error(err);
      alert('Failed to upload note.');
    }
  };

  const handleDelete = async (index, id) => {
    try {
      await axios.delete(`http://localhost:5000/api/notes/${id}`);
      const updated = notes.filter((_, i) => i !== index);
      setNotes(updated);
    } catch (err) {
      console.error('Failed to delete note:', err);
      alert('Error deleting note.');
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-6 text-blue-700">📚 Manage Notes</h2>

      <button
        onClick={() => setShowForm(!showForm)}
        className="mb-6 flex items-center gap-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        <FaPlus /> {showForm ? 'Cancel' : 'Add Note'}
      </button>

      {showForm && (
        <div className="mb-8 bg-gray-100 p-6 rounded-xl shadow space-y-4">
          {['university', 'course', 'semester', 'subject'].map((field) => (
            <input
              key={field}
              type={field === 'semester' ? 'number' : 'text'}
              name={field}
              placeholder={field.charAt(0).toUpperCase() + field.slice(1)}
              value={newNote[field]}
              onChange={handleChange}
              className="w-full border px-3 py-2 rounded focus:outline-none focus:ring focus:ring-blue-300"
            />
          ))}

          <div>
            <label className="block mb-1 font-medium text-gray-700">Upload PDF</label>
            <label className="bg-blue-600 text-white px-4 py-2 rounded cursor-pointer hover:bg-blue-700 inline-flex items-center gap-2">
              <FaUpload />
              Choose File
              <input
                type="file"
                accept="application/pdf"
                onChange={handleFileChange}
                className="hidden"
              />
            </label>
            {newNote.pdf && (
              <span className="ml-3 text-sm text-gray-700">{newNote.pdf.name}</span>
            )}
          </div>

          <button
            onClick={handleAdd}
            className="bg-green-600 text-white px-5 py-2 rounded hover:bg-green-700"
          >
            Submit Note
          </button>
        </div>
      )}

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {notes.map((note, index) => (
          <div
            key={note._id}
            className="bg-white rounded-xl shadow p-4 border border-gray-200 dark:bg-gray-800 dark:border-gray-700"
          >
            <h3 className="text-xl font-semibold text-blue-800 mb-1">{note.subject}</h3>
            <p className="text-sm text-gray-600 dark:text-gray-400 mb-1">
              {note.course} | Semester {note.semester}
            </p>
            <p className="text-sm text-gray-500 dark:text-gray-400 mb-3">{note.university}</p>
           
            <a
              href={`http://localhost:5000/uploads/${note.pdf}`}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-flex items-center gap-2 text-blue-600 text-sm hover:underline"
            >
              <FaFilePdf /> View PDF
            </a>

            <button
              onClick={() => handleDelete(index, note._id)}
              className="mt-3 inline-flex items-center gap-1 bg-red-500 text-white text-sm px-3 py-1 rounded hover:bg-red-600"
            >
              <FaTrash /> Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Notes;
