import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import { FaBars, FaTachometerAlt, FaBookOpen, FaUniversity, FaUser, FaTimes } from 'react-icons/fa';

const UserSidebar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => setIsOpen(!isOpen);

  const baseStyle =
    'flex items-center gap-3 px-4 py-2.5 rounded-lg transition duration-150 ease-in-out hover:scale-[1.02]';

  const activeStyle =
    'bg-gradient-to-r from-blue-600 to-blue-500 text-white font-semibold shadow-md';

  const sectionTitle = (title, color = 'bg-gray-100 dark:bg-gray-800') => (
    <div
      className={`flex items-center gap-2 mt-6 mb-3 px-4 py-1.5 text-xs font-semibold uppercase tracking-wider text-gray-600 dark:text-gray-300 rounded-md ${color} shadow-sm`}
    >
      <span className="w-2 h-2 rounded-full bg-blue-500 animate-pulse"></span>
      {title}
    </div>
  );

  return (
    <>
      {/* Mobile Toggle Button */}
      <div className="lg:hidden p-4 flex items-center justify-between bg-white dark:bg-gray-900 shadow-md z-50">
        <h2 className="text-xl font-bold text-blue-700 dark:text-white">ðŸŽ“ Academic Source</h2>
        <button
          className="text-2xl text-blue-700 dark:text-white focus:outline-none"
          onClick={toggleSidebar}
        >
          {isOpen ? <FaTimes /> : <FaBars />}
        </button>
      </div>

      {/* Sidebar */}
      <aside
        className={`${
          isOpen ? 'translate-x-0' : '-translate-x-full'
        } lg:translate-x-0 fixed lg:static top-0 left-0 h-screen w-64 bg-white dark:bg-gray-900 border-r border-gray-200 dark:border-gray-700 shadow-xl transform transition-transform duration-300 z-40`}
      >
        {/* Logo Section */}
        <div className="p-6 text-center border-b border-gray-200 dark:border-gray-700">
          <h2 className="text-2xl font-extrabold text-blue-700 dark:text-white tracking-tight">
            ðŸŽ“ Academic Source
          </h2>
          <p className="text-xs text-gray-500 dark:text-gray-400 mt-1">User Panel</p>
        </div>

        {/* Navigation Links */}
        <nav className="flex flex-col text-sm text-gray-800 dark:text-gray-200 p-4">
          {sectionTitle('Overview', 'bg-blue-100 dark:bg-blue-950')}

          <NavLink
            to="/user"
            onClick={() => setIsOpen(false)}
            className={({ isActive }) =>
              `${baseStyle} ${isActive ? activeStyle : 'hover:bg-blue-50 dark:hover:bg-gray-700'}`
            }
          >
            <FaTachometerAlt />
            <span>Dashboard</span>
          </NavLink>

          <NavLink
            to="/user/profile"
            onClick={() => setIsOpen(false)}
            className={({ isActive }) =>
              `${baseStyle} ${isActive ? activeStyle : 'hover:bg-blue-50 dark:hover:bg-gray-700'}`
            }
          >
            <FaUser />
            <span>My Profile</span>
          </NavLink>

          <NavLink
            to="/user/resources"
            onClick={() => setIsOpen(false)}
            className={({ isActive }) =>
              `${baseStyle} ${isActive ? activeStyle : 'hover:bg-blue-50 dark:hover:bg-gray-700'}`
            }
          >
            <FaBookOpen />
            <span>My Resources</span>
          </NavLink>

          <NavLink
            to="/user/university"
            onClick={() => setIsOpen(false)}
            className={({ isActive }) =>
              `${baseStyle} ${isActive ? activeStyle : 'hover:bg-blue-50 dark:hover:bg-gray-700'}`
            }
          >
            <FaUniversity />
            <span>University Info</span>
          </NavLink>
        </nav>
      </aside>
    </>
  );
};

export default UserSidebar;
