// src/pages/CourseDetailsPage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const CourseDetailsPage = () => {
  const { universityId, department, courseName } = useParams();
  const [semesterCount, setSemesterCount] = useState(0);
  const [resources, setResources] = useState({});
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // fetch semester count based on course (you can enhance this)
    axios.get(`http://localhost:5000/api/universities/${universityId}`)
      .then(res => {
        const uni = res.data;
        const dept = uni.departments.find(d => d.name === department);
        const course = dept?.courses.find(c => c.name === courseName);
        if (course) setSemesterCount(course.semesters);
      }).catch(err => console.error('Error fetching course:', err));
  }, [universityId, department, courseName]);

  const fetchResources = async (semester) => {
    setLoading(true);
    try {
      const res = await axios.get(`http://localhost:5000/api/resources/${universityId}/${department}/${courseName}/${semester}`);
      setResources({ ...resources, [semester]: res.data });
    } catch (err) {
      console.error('Error fetching resources:', err);
    }
    setLoading(false);
  };

  return (
    <div className="p-6 min-h-screen bg-gray-50 text-gray-800 dark:bg-gray-900 dark:text-white">
      <h1 className="text-3xl font-bold text-blue-700 mb-6">{courseName} — {department}</h1>

      {semesterCount > 0 ? (
        <div className="space-y-6">
          {[...Array(semesterCount)].map((_, i) => {
            const sem = i + 1;
            const semData = resources[sem];

            return (
              <div key={sem} className="bg-white dark:bg-gray-800 rounded-xl shadow p-5 border">
                <h2 className="text-xl font-bold text-blue-600 mb-2">Semester {sem}</h2>
                <button
                  className="mb-4 px-4 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
                  onClick={() => fetchResources(sem)}
                >
                  {loading && !resources[sem] ? 'Loading...' : 'Explore Resources'}
                </button>

                {semData && (
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-4">
                    {/* Syllabus */}
                    <div>
                      <h3 className="font-semibold text-lg text-blue-500">📘 Syllabus</h3>
                      <ul className="text-sm">
                        {semData.syllabus?.map((syll, idx) => (
                          <li key={idx}>
                            <a href={syll.pdf} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline">
                              {syll.title}
                            </a>
                          </li>
                        )) || <li>No syllabus found</li>}
                      </ul>
                    </div>

                    {/* Notes */}
                    <div>
                      <h3 className="font-semibold text-lg text-green-500">📝 Notes</h3>
                      <ul className="text-sm">
                        {semData.notes?.map((note, idx) => (
                          <li key={idx}>
                            <a href={note.pdf} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline">
                              {note.title}
                            </a>
                          </li>
                        )) || <li>No notes found</li>}
                      </ul>
                    </div>

                    {/* Papers */}
                    <div>
                      <h3 className="font-semibold text-lg text-red-500">📄 Question Papers</h3>
                      <ul className="text-sm">
                        {semData.papers?.map((paper, idx) => (
                          <li key={idx}>
                            <a href={paper.pdf} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline">
                              {paper.title} ({paper.year})
                            </a>
                          </li>
                        )) || <li>No papers found</li>}
                      </ul>
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      ) : (
        <p className="text-gray-500">No semester data available.</p>
      )}
    </div>
  );
};

export default CourseDetailsPage;
