import React, { useState, useEffect } from 'react';
import { FaUserCircle } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import axios from 'axios';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
  withCredentials: true, // if you're using cookies for auth
});

const AdminNavbar = () => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [adminName, setAdminName] = useState('Admin');

  useEffect(() => {
    const fetchAdmin = async () => {
      try {
        const res = await API.get('/api/admin/me', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('adminToken')}`,
          },
        });
        setAdminName(res.data.name);
      } catch (err) {
        console.error('Failed to fetch admin info', err);
      }
    };

    fetchAdmin();
  }, []);

  return (
    <header className="bg-white dark:bg-gray-900 shadow-md px-6 py-4 flex items-center justify-between relative">
      {/* Centered Title */}
      <div className="absolute left-1/2 transform -translate-x-1/2">
        <h1 className="text-xl sm:text-2xl font-bold text-blue-700 dark:text-white tracking-tight">
          ðŸŽ“ Academic Source
        </h1>
      </div>

      {/* Right: Admin Info and Dropdown */}
      <div className="ml-auto flex items-center gap-3 text-gray-700 dark:text-gray-300 text-sm relative">
        <span className="hidden sm:inline">Welcome, {adminName}</span>
        <button
          onClick={() => setDropdownOpen(!dropdownOpen)}
          className="focus:outline-none"
        >
          <FaUserCircle className="text-2xl text-blue-600 dark:text-white" />
        </button>

        {/* Dropdown Menu */}
        {dropdownOpen && (
          <div className="absolute right-0 top-12 bg-white dark:bg-gray-800 border dark:border-gray-700 rounded shadow-lg py-2 w-40 z-50">
            <Link
              to="/admin/logout"
              className="block px-4 py-2 text-sm text-gray-700 dark:text-gray-200 hover:bg-gray-100 dark:hover:bg-gray-700"
              onClick={() => setDropdownOpen(false)}
            >
              ðŸ”’ Logout
            </Link>
          </div>
        )}
      </div>
    </header>
  );
};

export default AdminNavbar;
