import React, { useState } from 'react';
import axios from 'axios';

const AddSyllabus = () => {
  const [formData, setFormData] = useState({
    university: '',
    course: '',
    semester: '',
    subject: '',
    pdf: null
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({ ...formData, [name]: files ? files[0] : value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.pdf) {
      alert('Please upload a PDF file.');
      return;
    }

    const data = new FormData();
    data.append('university', formData.university);
    data.append('course', formData.course);
    data.append('semester', formData.semester);
    data.append('subject', formData.subject);
    data.append('pdf', formData.pdf);

    try {
      await axios.post('http://localhost:5000/api/syllabus', data);
      alert('✅ Syllabus submitted successfully!');
      setFormData({
        university: '',
        course: '',
        semester: '',
        subject: '',
        pdf: null
      });
    } catch (err) {
      console.error(err);
      alert('❌ Failed to submit syllabus.');
    }
  };

  return (
    <div className="max-w-xl mx-auto bg-white p-6 rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Add Syllabus</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block mb-1 font-medium">University</label>
          <input
            type="text"
            name="university"
            value={formData.university}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-medium">Course</label>
          <input
            type="text"
            name="course"
            value={formData.course}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-medium">Semester</label>
          <input
            type="text"
            name="semester"
            value={formData.semester}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-medium">Subject</label>
          <input
            type="text"
            name="subject"
            value={formData.subject}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-medium">Upload PDF</label>
          <input
            type="file"
            name="pdf"
            accept="application/pdf"
            onChange={handleChange}
            className="w-full"
            required
          />
          {formData.pdf && (
            <p className="text-sm text-gray-600 mt-1">{formData.pdf.name}</p>
          )}
        </div>

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddSyllabus;
