import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  FaTrash,
  FaUserShield,
  FaPlusCircle,
} from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE || 'http://localhost:5000/api',
});

const AdminTrusted = () => {
  const [trustedPersons, setTrustedPersons] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    position: '',
    description: '',
    email: '',
    image: null,
  });

  const fetchTrustedPersons = async () => {
    try {
      const res = await API.get('/trusted');
      setTrustedPersons(res.data);
    } catch (err) {
      console.error('Error fetching trusted persons:', err);
    }
  };

  useEffect(() => {
    fetchTrustedPersons();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure to delete?')) return;
    try {
      await API.delete(`/trusted/${id}`);
      fetchTrustedPersons();
    } catch (err) {
      console.error('Delete failed:', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.image) return alert('Please upload an image');

    const data = new FormData();
    for (const key in formData) {
      data.append(key, formData[key]);
    }

    try {
      await API.post('/trusted', data);
      setFormData({ name: '', position: '', description: '', email: '', image: null });
      fetchTrustedPersons();
    } catch (err) {
      console.error('Upload failed:', err);
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4 flex items-center gap-2">
        <FaUserShield /> Manage Trusted Persons
      </h2>

      {/* Form */}
      <form onSubmit={handleSubmit} className="bg-gray-100 p-4 rounded mb-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input
            type="text"
            placeholder="Name"
            value={formData.name}
            required
            className="p-2 border rounded"
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          />
          <input
            type="text"
            placeholder="Position"
            value={formData.position}
            required
            className="p-2 border rounded"
            onChange={(e) => setFormData({ ...formData, position: e.target.value })}
          />
          <input
            type="email"
            placeholder="Email"
            value={formData.email}
            required
            className="p-2 border rounded"
            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          />
          <input
            type="file"
            accept="image/*"
            required
            className="p-2 border rounded"
            onChange={(e) => setFormData({ ...formData, image: e.target.files[0] })}
          />
        </div>
        <textarea
          placeholder="Description (optional)"
          value={formData.description}
          className="p-2 border rounded w-full mt-4"
          onChange={(e) => setFormData({ ...formData, description: e.target.value })}
        />
        <button
          type="submit"
          className="mt-4 px-4 py-2 bg-blue-600 text-white rounded flex items-center gap-2"
        >
          <FaPlusCircle /> Add Trusted Person
        </button>
      </form>

      {/* Display List */}
      <div className="grid gap-4">
        {trustedPersons.map((person) => (
          <div key={person._id} className="bg-white p-4 border rounded flex items-center justify-between">
            <div className="flex items-center gap-4">
              <img
                src={API.defaults.baseURL + person.image}
                alt={person.name}
                style={{ width: '100px', height: '60px', objectFit: 'cover' }}
                className="rounded border"
              />
              <div>
                <h3 className="text-lg font-semibold">{person.name}</h3>
                <p className="text-sm text-gray-600">{person.position}</p>
                <p className="text-sm text-gray-500">{person.email}</p>
                {person.description && (
                  <p className="text-xs mt-1 text-gray-400">{person.description}</p>
                )}
              </div>
            </div>
            <button
              onClick={() => handleDelete(person._id)}
              className="text-red-600 hover:text-red-800"
            >
              <FaTrash />
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminTrusted;
