// src/admin-pages/Notes.js
import React, { useEffect, useState } from 'react';
import { FaTrash, FaFilePdf } from 'react-icons/fa';
import axios from 'axios';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const Notes = () => {
  const [notes, setNotes] = useState({});

  useEffect(() => {
    fetchNotes();
  }, []);

  const fetchNotes = async () => {
    try {
      const res = await API.get('/api/resources/notes');
      const groupedByUniversity = res.data.reduce((acc, note) => {
        if (!acc[note.university]) acc[note.university] = {};
        if (!acc[note.university][note.course]) acc[note.university][note.course] = [];
        acc[note.university][note.course].push(note);
        return acc;
      }, {});
      setNotes(groupedByUniversity);
    } catch (err) {
      console.error('‚ùå Failed to fetch notes:', err);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this note?')) return;

    try {
      await API.delete(`/api/resources/${id}`);
      fetchNotes(); // Refresh after deletion
    } catch (err) {
      console.error('‚ùå Failed to delete note:', err);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-6 text-blue-700">üìö All Notes</h2>

      {Object.keys(notes).length === 0 ? (
        <p className="text-gray-600">No notes found.</p>
      ) : (
        Object.entries(notes).map(([university, courses]) => (
          <div key={university} className="mb-10 border rounded-lg shadow p-4 bg-gray-50">
            <h3 className="text-2xl font-semibold text-green-700 mb-4 border-b pb-2">{university}</h3>

            {Object.entries(courses).map(([course, courseNotes]) => (
              <div key={course} className="mb-6">
                <h4 className="text-xl font-semibold text-blue-800 mb-3">{course}</h4>
                <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {courseNotes.map((note) => (
                    <div
                      key={note._id}
                      className="bg-white shadow-md p-4 rounded-lg border border-gray-200 transition-transform hover:scale-[1.02]"
                    >
                      <h4 className="text-lg font-bold text-blue-600 mb-1">
                        {note.title || note.subject}
                      </h4>
                      <p className="text-sm text-gray-600">Semester: {note.semester}</p>
                      <p className="text-sm text-gray-600">Department: {note.departmentName}</p>

                      <a
                        href={`${process.env.REACT_APP_API_BASE}/uploads/resources/${note.file}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="inline-flex items-center gap-2 text-blue-600 hover:underline text-sm mt-2"
                      >
                        <FaFilePdf /> View PDF
                      </a>

                      <button
                        onClick={() => handleDelete(note._id)}
                        className="mt-3 bg-red-500 text-white text-sm px-3 py-1 rounded hover:bg-red-600 flex items-center gap-1"
                      >
                        <FaTrash /> Delete
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  );
};

export default Notes;
