import axios from 'axios';

// Inside AddPaper component:
const handleSubmit = async (e) => {
  e.preventDefault();

  // Create FormData object
  const data = new FormData();
  data.append('university', formData.university);
  data.append('course', formData.course);
  data.append('year', formData.year);
  data.append('subject', formData.subject);
  data.append('pdf', formData.pdf); // actual file

  try {
    const res = await axios.post('http://localhost:5000/api/papers', data, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    });
    alert('✅ Question paper submitted!');
    console.log('Response:', res.data);
  } catch (err) {
    console.error('Error uploading paper:', err);
    alert('❌ Failed to submit paper.');
  }
};
