import React, { useState } from 'react';

const allResources = [
  // üåê Tutorials
  {
    category: 'Tutorials',
    title: 'GeeksforGeeks',
    description: 'Portal for DSA, web dev, placement prep and more.',
    link: 'https://www.geeksforgeeks.org/',
    image: 'https://media.geeksforgeeks.org/wp-content/cdn-uploads/20230313172301/gfglogo-300x300.png'
  },
  {
    category: 'Tutorials',
    title: 'W3Schools',
    description: 'HTML, CSS, JS, Python and backend tutorials.',
    link: 'https://www.w3schools.com/',
    image: 'https://www.w3schools.com/images/w3schools_green.jpg'
  },

  // üìö eLibrary
  {
    category: 'eLibrary',
    title: 'National Digital Library of India',
    description: 'Access academic books, journals, theses, and more.',
    link: 'https://ndl.iitkgp.ac.in/',
    image: 'https://ndl.iitkgp.ac.in/assets/images/ndl_logo.png'
  },
  {
    category: 'eLibrary',
    title: 'Open Library',
    description: 'Read or borrow eBooks for free from millions of titles.',
    link: 'https://openlibrary.org/',
    image: 'https://openlibrary.org/static/images/openlibrary-logo-tighter.svg'
  },

  // üìò eBooks
  {
    category: 'eBooks',
    title: 'PDF Drive',
    description: 'Search and download millions of free eBooks.',
    link: 'https://www.pdfdrive.com/',
    image: 'https://www.pdfdrive.com/assets/images/logo.png'
  },
  {
    category: 'eBooks',
    title: 'Bookboon',
    description: 'Free textbooks and business eBooks for students and professionals.',
    link: 'https://bookboon.com/',
    image: 'https://bookboon.com/images/logo.png'
  },

  // üé• YouTube Learning
  {
    category: 'YouTube',
    title: 'Apna College',
    description: 'DSA + Web Dev free courses in Hindi.',
    link: 'https://www.youtube.com/@ApnaCollegeOfficial',
    image: 'https://yt3.googleusercontent.com/ytc/APkrFKbWzG7lEB2kHk1UTcUSwqv8FCj_5If3W7Hb9fvb=s900-c-k-c0x00ffffff-no-rj'
  },
  {
    category: 'YouTube',
    title: 'Gate Smashers',
    description: 'Computer Science subjects in detail (OS, DBMS, CN, etc).',
    link: 'https://www.youtube.com/@GateSmashers',
    image: 'https://yt3.googleusercontent.com/ytc/APkrFKbV5kT6P1zPMRSMbKec7Ep4Z9CqefUJnQxtKrHk=s900-c-k-c0x00ffffff-no-rj'
  },

  // üåç MOOCs
  {
    category: 'MOOCs',
    title: 'Coursera',
    description: 'Courses from top universities like Stanford, Google, and IBM.',
    link: 'https://www.coursera.org/',
    image: 'https://about.coursera.org/static/whiteCourseraLogo.png'
  },
  {
    category: 'MOOCs',
    title: 'edX',
    description: 'Top university courses for free or with certificates.',
    link: 'https://www.edx.org/',
    image: 'https://upload.wikimedia.org/wikipedia/commons/4/4f/EdX.svg'
  },
];

const EPlatform = () => {
  const [searchTerm, setSearchTerm] = useState('');

  // Filter resources based on search term
  const filteredResources = allResources.filter(resource =>
    resource.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    resource.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-white py-10 px-4">
      <h1 className="text-4xl font-bold text-center text-blue-600 mb-6">E-Learning Resources</h1>

      {/* üîç Search Input */}
      <div className="max-w-3xl mx-auto mb-10">
        <input
          type="text"
          placeholder="Search eBooks, libraries, tutorials..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full px-6 py-3 border border-blue-300 rounded-2xl focus:outline-none focus:ring-2 focus:ring-blue-400 shadow"
        />
      </div>

      {/* üß† Resource Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {filteredResources.map((resource, index) => (
          <div key={index} className="bg-white shadow-md rounded-2xl p-5 hover:shadow-xl transition duration-300 flex flex-col">
            <img src={resource.image} alt={resource.title} className="w-full h-40 object-contain mb-4 rounded" />
            <h2 className="text-xl font-semibold text-gray-800">{resource.title}</h2>
            <p className="text-gray-600 text-sm flex-grow">{resource.description}</p>
            <span className="mt-2 text-xs inline-block bg-blue-100 text-blue-700 px-3 py-1 rounded-full">
              {resource.category}
            </span>
            <a
              href={resource.link}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-block mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
            >
              Visit
            </a>
          </div>
        ))}
      </div>

      {/* No match found */}
      {filteredResources.length === 0 && (
        <p className="text-center text-gray-600 mt-10 text-lg">No resources found for "<strong>{searchTerm}</strong>"</p>
      )}
    </div>
  );
};

export default EPlatform;
