import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTrash, FaDownload } from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const ManageNotes = () => {
  const [notes, setNotes] = useState([]);

  const fetchNotes = async () => {
    try {
      const res = await API.get('/api/resources');
      const filteredNotes = res.data.filter(item => item.type === 'notes');
      setNotes(filteredNotes);
    } catch (err) {
      console.error('Failed to fetch notes:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await API.delete(`/api/resources/${id}`);
      setNotes(prev => prev.filter(note => note._id !== id));
    } catch (err) {
      console.error('Error deleting note:', err);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">Manage Notes</h2>
      
      {notes.length === 0 ? (
        <p className="text-gray-600">No notes uploaded.</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
          {notes.map((note) => (
            <div
              key={note._id}
              className="bg-white p-4 rounded-2xl shadow-md flex flex-col justify-between"
            >
              <div>
                <h3 className="text-lg font-semibold truncate text-gray-700">{note.title || note.filename}</h3>
                <p className="text-sm text-gray-500 mt-1">Uploaded: {new Date(note.createdAt).toLocaleDateString()}</p>
              </div>
              <div className="flex justify-end items-center mt-4 space-x-4">
                <a
                  href={`${process.env.REACT_APP_API_BASE}/${note.filePath}`}
                  download
                  className="text-green-600 hover:text-green-800"
                  title="Download"
                >
                  <FaDownload size={18} />
                </a>
                <button
                  onClick={() => handleDelete(note._id)}
                  className="text-red-600 hover:text-red-800"
                  title="Delete"
                >
                  <FaTrash size={18} />
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ManageNotes;
