import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faBars,
  faMoon,
  faSun
} from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';

export default function Navbar() {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => setDarkMode(!darkMode);

  useEffect(() => {
    const root = window.document.documentElement;
    darkMode ? root.classList.add('dark') : root.classList.remove('dark');
  }, [darkMode]);

  return (
    <>
      {/* Top Navbar */}
      <header className="sticky top-0 bg-white dark:bg-gray-900 text-black dark:text-white p-3 shadow flex justify-between items-center z-40">
        <h1 className="text-lg font-semibold">ARMS</h1>
        <nav className="flex gap-4">
          <Link to="/" className="hover:underline">Home</Link>
          <Link to="/departments" className="hover:underline">Departments</Link>
          <Link to="/university" className="hover:underline">University</Link>
          <Link to="/contact" className="hover:underline">Contact Us</Link>
          <Link to="/members" className="hover:underline">Special Members</Link>
          <Link to="/admin" className="hover:underline">Admin Login</Link>
        </nav>
        <button onClick={toggleDarkMode} className="text-xl">
          <FontAwesomeIcon icon={darkMode ? faSun : faMoon} />
        </button>
      </header>

      {/* Main Content Placeholder */}
      <main className="p-4 dark:bg-gray-800 dark:text-white bg-gray-100 min-h-screen">
        <p>Welcome to ARMS Portal!</p>
      </main>
    </>
  );
}
