import React, { useEffect, useState } from 'react';
import { FaUserTie, FaEnvelope, FaPhoneAlt } from 'react-icons/fa';
import axios from 'axios';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const TrustedPage = () => {
  const [trustedPersons, setTrustedPersons] = useState([]);
  const [purchasers, setPurchasers] = useState([]);

  useEffect(() => {
    fetchTrusted();
    fetchPurchasers();
  }, []);

  const fetchTrusted = async () => {
    try {
      const res = await API.get('/api/trusted'); // Make sure your backend route exists
      setTrustedPersons(res.data);
    } catch (err) {
      console.error('Error fetching trusted persons', err);
    }
  };

  const fetchPurchasers = async () => {
    try {
      const res = await API.get('/api/purchasers'); // Make sure your backend route exists
      setPurchasers(res.data);
    } catch (err) {
      console.error('Error fetching purchasers', err);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-6 text-center text-blue-700">Our Trusted Persons</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 mb-12">
        {trustedPersons.map((person) => (
          <div
            key={person._id}
            className="bg-white shadow-lg rounded-lg p-4 flex flex-col items-center text-center"
          >
            <img
              src={`${process.env.REACT_APP_API_BASE}${person.image}`}
              alt={person.name}
              className="w-32 h-32 rounded-full object-cover mb-4 border border-gray-300"
            />
            <h3 className="text-xl font-semibold">{person.name}</h3>
            <p className="text-gray-600 flex items-center gap-2 mt-1">
              <FaEnvelope /> {person.email}
            </p>
            <p className="text-gray-600 flex items-center gap-2">
              <FaPhoneAlt /> {person.phone}
            </p>
          </div>
        ))}
      </div>

      <h2 className="text-3xl font-bold mb-6 text-center text-green-700">Book Purchasers</h2>
      <div className="bg-gray-50 shadow-md rounded-lg p-4">
        {purchasers.length === 0 ? (
          <p className="text-center text-gray-500">No purchasers found.</p>
        ) : (
          <ul className="space-y-2">
            {purchasers.map((buyer) => (
              <li key={buyer._id} className="border-b py-2">
                <span className="font-semibold">{buyer.name}</span> â€” {buyer.email} ({buyer.bookName})
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default TrustedPage;
