import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  FaUpload,
  FaUniversity,
  FaBuilding,
  FaBook,
  FaLayerGroup,
  FaClipboardList,
  FaFilePdf,
} from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const UploadResources = () => {
  const [universities, setUniversities] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [courses, setCourses] = useState([]);
  const [semesterOptions, setSemesterOptions] = useState([]);

  const [formData, setFormData] = useState({
    university: '',
    department: '',
    course: '',
    semester: '',
    type: 'note', // singular, matches schema: note, paper, syllabus
    title: '',
    file: null,
  });

  // Fetch universities once on mount
  useEffect(() => {
    API.get('/api/universities')
      .then((res) => setUniversities(res.data))
      .catch((err) => console.error('Failed to fetch universities:', err));
  }, []);

  // Update departments when university changes
  useEffect(() => {
    const selectedUni = universities.find((u) => u._id === formData.university);
    if (selectedUni) {
      setDepartments(selectedUni.departments || []);
      setFormData((prev) => ({
        ...prev,
        department: '',
        course: '',
        semester: '',
      }));
      setCourses([]);
      setSemesterOptions([]);
    }
  }, [formData.university, universities]);

  // Update courses when department changes
  useEffect(() => {
    const selectedDept = departments.find((d) => d.name === formData.department);
    if (selectedDept) {
      setCourses(selectedDept.courses || []);
      setFormData((prev) => ({
        ...prev,
        course: '',
        semester: '',
      }));
      setSemesterOptions([]);
    }
  }, [formData.department, departments]);

  // Update semester options when course changes
  useEffect(() => {
    const selectedCourse = courses.find((c) => c.name === formData.course);
    if (selectedCourse) {
      const totalSemesters = Number(selectedCourse.semesters) || 0;
      const semesters = Array.from({ length: totalSemesters }, (_, i) => i + 1);
      setSemesterOptions(semesters);
    } else {
      setSemesterOptions([]);
    }
  }, [formData.course, courses]);

  // Handle input changes for select and text inputs
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handle file selection
  const handleFileChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      file: e.target.files[0] || null,
    }));
  };

  // Submit form and upload file + data
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.file) {
      alert('Please select a PDF file to upload.');
      return;
    }

    const data = new FormData();

    // Append form fields except file
    data.append('university', formData.university);
    data.append('department', formData.department);
    data.append('course', formData.course);
    data.append('semester', formData.semester);
    data.append('type', formData.type);
    data.append('title', formData.title);

    // Append file
    data.append('file', formData.file);

    try {
      const res = await API.post('/api/resources/upload', data, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      alert('✅ Uploaded successfully');

      // Reset form after upload
      setFormData({
        university: '',
        department: '',
        course: '',
        semester: '',
        type: 'note',
        title: '',
        file: null,
      });
      setDepartments([]);
      setCourses([]);
      setSemesterOptions([]);
    } catch (err) {
      console.error('Upload error:', err.response?.data || err.message);
      alert('❌ Failed to upload resource');
    }
  };

  return (
    <div className="p-8 max-w-2xl mx-auto bg-white shadow-xl rounded-xl">
      <h2 className="text-3xl font-bold mb-6 text-center text-blue-700 flex items-center justify-center gap-3">
        <FaUpload className="text-4xl text-green-600" /> Upload Academic Resource
      </h2>

      <form onSubmit={handleSubmit} className="space-y-5">
        {/* University */}
        <div>
          <label className="block mb-1 font-medium flex items-center gap-2">
            <FaUniversity /> University
          </label>
          <select
            name="university"
            value={formData.university}
            onChange={handleChange}
            required
            className="w-full border p-2 rounded"
          >
            <option value="">Select University</option>
            {universities.map((u) => (
              <option key={u._id} value={u._id}>
                {u.name}
              </option>
            ))}
          </select>
        </div>

        {/* Department */}
        <div>
          <label className="block mb-1 font-medium flex items-center gap-2">
            <FaBuilding /> Department
          </label>
          <select
            name="department"
            value={formData.department}
            onChange={handleChange}
            required
            disabled={!departments.length}
            className="w-full border p-2 rounded"
          >
            <option value="">Select Department</option>
            {departments.map((dept, i) => (
              <option key={i} value={dept.name}>
                {dept.name}
              </option>
            ))}
          </select>
        </div>

        {/* Course */}
        <div>
          <label className="block mb-1 font-medium flex items-center gap-2">
            <FaBook /> Course
          </label>
          <select
            name="course"
            value={formData.course}
            onChange={handleChange}
            required
            disabled={!courses.length}
            className="w-full border p-2 rounded"
          >
            <option value="">Select Course</option>
            {courses.map((course, i) => (
              <option key={i} value={course.name}>
                {course.name}
              </option>
            ))}
          </select>
        </div>

        {/* Semester */}
        <div>
          <label className="block mb-1 font-medium flex items-center gap-2">
            <FaLayerGroup /> Semester
          </label>
          <select
            name="semester"
            value={formData.semester}
            onChange={handleChange}
            required
            disabled={!semesterOptions.length}
            className="w-full border p-2 rounded"
          >
            <option value="">Select Semester</option>
            {semesterOptions.map((num) => (
              <option key={num} value={num}>
                Semester {num}
              </option>
            ))}
          </select>
        </div>

        {/* Resource Type */}
        <div>
          <label className="block mb-1 font-medium flex items-center gap-2">
            <FaClipboardList /> Resource Type
          </label>
          <select
            name="type"
            value={formData.type}
            onChange={handleChange}
            className="w-full border p-2 rounded"
          >
            <option value="note">Notes</option>
            <option value="paper">Question Paper</option>
            <option value="syllabus">Syllabus</option>
          </select>
        </div>

        {/* Title */}
        <div>
          <label className="block mb-1 font-medium">Title / Subject</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            placeholder="e.g. Data Structures, Operating Systems"
            required
          />
        </div>

        {/* File Upload */}
        <div>
          <label className="block mb-1 font-medium flex items-center gap-2">
            <FaFilePdf /> Upload PDF
          </label>
          <input
            type="file"
            accept="application/pdf"
            onChange={handleFileChange}
            required
            className="w-full border p-2 rounded"
          />
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          className="w-full bg-blue-600 text-white font-semibold py-2 px-4 rounded hover:bg-blue-700 flex items-center justify-center gap-2"
          disabled={!formData.file}
        >
          <FaUpload /> Submit Resource
        </button>
      </form>
    </div>
  );
};

export default UploadResources;
