// src/admin-pages/ManageNotes.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTrash, FaDownload, FaUniversity } from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const ManageNotes = () => {
  const [notes, setNotes] = useState([]);

  const fetchNotes = async () => {
    try {
      const res = await API.get('/api/resources/notes');
      setNotes(res.data);
    } catch (err) {
      console.error('Failed to fetch notes:', err);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this note?')) return;
    try {
      await API.delete(`/api/resources/${id}`);
      alert('✅ Note deleted successfully.');
      fetchNotes();
    } catch (err) {
      console.error('❌ Delete failed:', err);
      alert('❌ Failed to delete note.');
    }
  };

  const handleDownload = async (note) => {
    try {
      const downloadUrl = `${process.env.REACT_APP_API_BASE}/api/resources/download/${note._id}`;
      window.open(downloadUrl, '_blank', 'noopener,noreferrer');
    } catch (err) {
      console.error('❌ Download failed:', err);
      alert('❌ Failed to download note.');
    }
  };

  const groupedByUniversity = notes.reduce((acc, note) => {
    if (!acc[note.universityName]) acc[note.universityName] = {};
    if (!acc[note.universityName][note.department]) acc[note.universityName][note.department] = [];
    acc[note.universityName][note.department].push(note);
    return acc;
  }, {});

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <h2 className="text-4xl font-bold text-center mb-10 text-blue-800">📚 Manage Uploaded Notes</h2>

      {Object.entries(groupedByUniversity).length === 0 ? (
        <p className="text-center text-gray-500">No notes available.</p>
      ) : (
        Object.entries(groupedByUniversity).map(([universityName, departments]) => (
          <div key={universityName} className="bg-white rounded-2xl shadow-lg border p-8 mb-12">
            <h3 className="text-3xl font-semibold text-center text-purple-700 mb-6 flex items-center justify-center gap-2">
              <FaUniversity className="text-purple-500" /> {universityName}
            </h3>

            {Object.entries(departments).map(([departmentName, notesList]) => (
              <div key={departmentName} className="mb-10">
                <h4 className="text-2xl font-medium text-center text-blue-600 mb-4">
                  📘 Department: {departmentName}
                </h4>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {notesList.map((note) => (
                    <div
                      key={note._id}
                      className="bg-gradient-to-br from-white to-blue-50 border border-blue-200 rounded-xl shadow-md p-5 hover:shadow-lg transition-all"
                    >
                      <div className="text-center mb-4">
                        <h5 className="text-xl font-semibold text-gray-800">{note.title}</h5>
                        <p className="text-sm text-gray-500 mt-1">
                          {note.course} - Semester {note.semester}
                        </p>
                      </div>

                      <div className="flex justify-center gap-4 mt-4">
                        <button
                          onClick={() => handleDownload(note)}
                          className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg shadow-md text-sm"
                        >
                          <FaDownload className="inline-block mr-2" /> Download
                        </button>

                        <button
                          onClick={() => handleDelete(note._id)}
                          className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg shadow-md text-sm"
                        >
                          <FaTrash className="inline-block mr-2" /> Delete
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  );
};

export default ManageNotes;
