import React, { useState } from 'react';
import { FaUserCircle, FaBars } from 'react-icons/fa';

const UserNavbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <header className="bg-white shadow-md px-4 py-3 sm:px-6 relative z-20">
      <div className="flex justify-between items-center">
        {/* Left: Logo / Title */}
        <div className="flex items-center gap-3">
          <button
            className="md:hidden text-gray-700 text-2xl"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          >
            <FaBars />
          </button>
          <h1 className="text-lg sm:text-xl font-bold text-blue-700">
            User Dashboard
          </h1>
        </div>

        {/* Right: User Info */}
        <div className="flex items-center gap-3">
          <FaUserCircle className="text-2xl text-gray-600" />
          <span className="hidden sm:block text-sm sm:text-base text-gray-700 font-medium">
            Welcome, User
          </span>
        </div>
      </div>

      {/* Mobile Dropdown Menu */}
      {isMenuOpen && (
        <div className="md:hidden mt-3 bg-white border border-gray-200 rounded-lg shadow-md p-4 space-y-2">
          {/* Add mobile-specific nav items here */}
          <button className="block w-full text-left text-gray-700 hover:text-blue-600">
            Dashboard
          </button>
          <button className="block w-full text-left text-gray-700 hover:text-blue-600">
            Profile
          </button>
          <button className="block w-full text-left text-gray-700 hover:text-blue-600">
            Logout
          </button>
        </div>
      )}
    </header>
  );
};

export default UserNavbar;
