import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTrash, FaDownload } from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const ManageNotes = () => {
  const [notes, setNotes] = useState([]);

  const fetchNotes = async () => {
    try {
      const res = await API.get('/api/resources');
      const filteredNotes = res.data.filter(item => item.type === 'notes');
      setNotes(filteredNotes);
    } catch (err) {
      console.error('Failed to fetch notes:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await API.delete(`/api/resources/${id}`);
      setNotes(prev => prev.filter(note => note._id !== id));
    } catch (err) {
      console.error('Error deleting note:', err);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Manage Notes</h2>
      {notes.length === 0 ? (
        <p>No notes uploaded.</p>
      ) : (
        <ul className="space-y-4">
          {notes.map((note) => (
            <li
              key={note._id}
              className="flex justify-between items-center bg-white p-4 shadow rounded-md"
            >
              <span className="truncate max-w-xs">{note.title || note.filename}</span>
              <div className="flex space-x-3">
                <a
                  href={`${process.env.REACT_APP_API_BASE}/${note.filePath}`}
                  download
                  className="text-green-600 hover:text-green-800"
                >
                  <FaDownload />
                </a>
                <button
                  onClick={() => handleDelete(note._id)}
                  className="text-red-600 hover:text-red-800"
                >
                  <FaTrash />
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ManageNotes;
