import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Notes = () => {
  const [notes, setNotes] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newNote, setNewNote] = useState({
    university: '',
    course: '',
    semester: '',
    subject: '',
    pdf: null
  });

  // ðŸ” Load notes from DB on component mount
  useEffect(() => {
    axios.get('http://localhost:5000/api/notes')
      .then(res => setNotes(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleFileChange = (e) => {
    setNewNote({ ...newNote, pdf: e.target.files[0] });
  };

  const handleChange = (e) => {
    setNewNote({ ...newNote, [e.target.name]: e.target.value });
  };

  const handleAdd = async () => {
    const { university, course, semester, subject, pdf } = newNote;

    if (!university || !course || !semester || !subject || !pdf) {
      return alert('Please fill all fields!');
    }

    const formData = new FormData();
    formData.append('university', university);
    formData.append('course', course);
    formData.append('semester', semester);
    formData.append('subject', subject);
    formData.append('pdf', pdf);

    try {
      const res = await axios.post('http://localhost:5000/api/notes', formData);
      setNotes([...notes, res.data.note]);
      setNewNote({ university: '', course: '', semester: '', subject: '', pdf: null });
      setShowForm(false);
    } catch (err) {
      console.error(err);
      alert('Failed to upload note.');
    }
  };

  const handleDelete = (index) => {
    const updated = notes.filter((_, i) => i !== index);
    setNotes(updated);
  };

  return (
    <div className="p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Manage Notes</h2>

      <button
        onClick={() => setShowForm(!showForm)}
        className="mb-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        {showForm ? 'Cancel' : 'Add Note'}
      </button>

      {showForm && (
        <div className="mb-6 bg-gray-100 p-4 rounded shadow space-y-3">
          <input
            type="text"
            name="university"
            placeholder="University"
            value={newNote.university}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="text"
            name="course"
            placeholder="Course"
            value={newNote.course}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="number"
            name="semester"
            placeholder="Semester"
            value={newNote.semester}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="text"
            name="subject"
            placeholder="Subject"
            value={newNote.subject}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
          />
          <div>
            <label className="block mb-1 font-medium">Upload PDF</label>
            <label className="bg-blue-600 text-white px-4 py-2 rounded cursor-pointer hover:bg-blue-700 inline-block">
              Choose File
              <input
                type="file"
                accept="application/pdf"
                onChange={handleFileChange}
                className="hidden"
              />
            </label>
            {newNote.pdf && <span className="ml-2 text-sm text-gray-700">{newNote.pdf.name}</span>}
          </div>
          <button
            onClick={handleAdd}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            Submit
          </button>
        </div>
      )}

      <div className="space-y-4">
        {notes.map((note, index) => (
          <div key={index} className="border p-4 rounded shadow-sm">
            <h3 className="text-lg font-semibold text-blue-700">{note.subject} (Sem {note.semester})</h3>
            <p className="text-sm text-gray-600">{note.course} - {note.university}</p>
            <a
              href={`http://localhost:5000/uploads/${note.pdf}`}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-500 text-sm hover:underline"
            >
              View PDF
            </a>
            <div className="mt-2">
              <button
                onClick={() => handleDelete(index)}
                className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600 text-sm"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Notes;
