import React from 'react';
import { useParams } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBook, faFileAlt, faListAlt } from '@fortawesome/free-solid-svg-icons';

const departmentData = {
  'sardar-patel-university-mandi': {
    notes: ['SPU DSA Notes', 'SPU DBMS Notes'],
    papers: {
      2022: ['SPU 2022 Midterm'],
      2023: ['SPU 2023 Endterm']
    },
    syllabus: ['SPU Semester 1', 'SPU Semester 2']
  },
  'himachal-pradesh-university': {
    notes: ['HPU Java Notes', 'HPU OS Notes'],
    papers: {
      2021: ['HPU 2021 Final'],
      2022: ['HPU 2022 Midterm']
    },
    syllabus: ['HPU Semester 3', 'HPU Semester 4']
  },
  'iit-mandi': {
    notes: ['IIT Python Notes', 'IIT Machine Learning Notes'],
    papers: {
      2023: ['IIT 2023 Spring'],
      2024: ['IIT 2024 Autumn']
    },
    syllabus: ['IIT B.Tech CSE', 'IIT M.Tech AI']
  },
  'nit-hamirpur': {
    notes: ['NIT Mechanics Notes', 'NIT Thermodynamics'],
    papers: {
      2022: ['NIT 2022 Semester'],
      2023: ['NIT 2023 Endterm']
    },
    syllabus: ['NIT B.Tech Mech', 'NIT B.Tech Civil']
  },
  'central-university-of-himachal-pradesh': {
    notes: ['CUHP English Notes', 'CUHP History Notes'],
    papers: {
      2022: ['CUHP 2022 Final'],
      2023: ['CUHP 2023 Midterm']
    },
    syllabus: ['CUHP UG', 'CUHP PG']
  }
};

const DepartmentPages = () => {
  const { name } = useParams();
  const formattedName = name.toLowerCase();
  const deptName = name.replace(/-/g, ' ').toUpperCase();
  const data = departmentData[formattedName] || { notes: [], papers: {}, syllabus: [] };

  return (
    <div className="p-8">
      <h2 className="text-2xl font-bold text-blue-800 mb-6">Departments for {deptName}</h2>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-white dark:bg-gray-800 p-4 rounded shadow">
          <h3 className="font-semibold text-lg mb-2"><FontAwesomeIcon icon={faBook} className="mr-2" />Notes</h3>
          <ul>
            {data.notes.map((item, idx) => (
              <li key={idx} className="mb-1">â€¢ {item}</li>
            ))}
          </ul>
        </div>

        <div className="bg-white dark:bg-gray-800 p-4 rounded shadow">
          <h3 className="font-semibold text-lg mb-2"><FontAwesomeIcon icon={faFileAlt} className="mr-2" />Question Papers</h3>
          {Object.entries(data.papers).map(([year, papers], idx) => (
            <div key={idx} className="mb-2">
              <h4 className="font-medium text-blue-700 dark:text-blue-400">{year}</h4>
              <ul className="ml-4 list-disc">
                {papers.map((paper, i) => (
                  <li key={i}>{paper}</li>
                ))}
              </ul>
            </div>
          ))}
        </div>

        <div className="bg-white dark:bg-gray-800 p-4 rounded shadow">
          <h3 className="font-semibold text-lg mb-2"><FontAwesomeIcon icon={faListAlt} className="mr-2" />Syllabus</h3>
          <ul>
            {data.syllabus.map((item, idx) => (
              <li key={idx} className="mb-1">â€¢ {item}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default DepartmentPages;
