import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const Footer = () => {
  const [date, setDate] = useState(new Date());
  const [darkMode, setDarkMode] = useState(false);

  const toggleReadingMode = () => {
    document.documentElement.classList.toggle('dark');
    setDarkMode(!darkMode);
  };

  return (
    <footer className="bg-blue-50 dark:bg-gray-900 text-gray-700 dark:text-gray-300 border-t border-gray-200 dark:border-gray-700 py-12 px-6 mt-10">
      <div className="max-w-7xl mx-auto grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-10 text-center sm:text-left">

        {/* 1. Brand Info */}
        <div>
          <h2 className="text-2xl font-bold text-blue-700 dark:text-white mb-2">ARMS Portal</h2>
          <p className="text-sm">
            Empowering students and faculty with centralized academic resources.
          </p>
          <p className="text-sm mt-4">
            &copy; {new Date().getFullYear()} <span className="text-blue-600 dark:text-blue-300">Gautav & Team</span>
          </p>
        </div>

        {/* 2. Quick Links */}
        <div>
          <h3 className="text-lg font-semibold text-blue-600 dark:text-blue-300 mb-3">Quick Links</h3>
          <ul className="space-y-2 text-sm">
            <li>
              <Link to="/" className="hover:text-blue-700 transition">Home</Link>
            </li>
            <li>
              <Link to="/aboutus" className="hover:text-blue-700 transition">About Us</Link>
            </li>
            <li>
              <Link to="/teams" className="hover:text-blue-700 transition">Our Team</Link>
            </li>
            <li>
              <Link to="/admin/login" className="hover:text-blue-700 transition">Admin Login</Link>
            </li>
            <li>
              <Link to="/dashboard" className="hover:text-blue-700 transition">Dashboard</Link>
            </li>
          </ul>
        </div>

        {/* 3. Calendar */}
        <div>
          <h3 className="text-lg font-semibold text-blue-600 dark:text-blue-300 mb-3">Calendar</h3>
          <div className="flex justify-center sm:justify-start">
            <Calendar
              onChange={setDate}
              value={date}
              className="rounded-xl p-2 bg-white dark:bg-gray-800 text-sm"
            />
          </div>
        </div>

        {/* 4. Reading Mode Toggle */}
        <div>
          <h3 className="text-lg font-semibold text-blue-600 dark:text-blue-300 mb-3">Reading Mode</h3>
          <button
            onClick={toggleReadingMode}
            className="mt-2 px-5 py-2 bg-blue-600 text-white rounded-xl shadow hover:bg-blue-700 transition"
          >
            {darkMode ? 'Disable Dark Mode' : 'Enable Dark Mode'}
          </button>
        </div>
      </div>

      {/* Bottom Text */}
      <div className="text-center mt-10 text-xs text-gray-500 dark:text-gray-400">
        All rights reserved © {new Date().getFullYear()} ARMS Portal
      </div>
    </footer>
  );
};

export default Footer;
