import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  FaUniversity,
  FaBook,
  FaFilePdf,
  FaUserEdit,
  FaKeyboard,
  FaRegStickyNote,
  FaInfoCircle,
} from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const AddNotes = () => {
  const [formData, setFormData] = useState({
    university: '', departmentName: '', courseName: '', subject: '',
    title: '', description: '', fileUrl: '', uploadedBy: ''
  });

  const [universities, setUniversities] = useState([]);

  useEffect(() => {
    API.get('/api/universities')
      .then(res => setUniversities(res.data))
      .catch(err => console.error('Error fetching universities:', err));
  }, []);

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await API.post('/api/notes', formData);
      alert('✅ Note uploaded!');
      setFormData({
        university: '', departmentName: '', courseName: '',
        subject: '', title: '', description: '', fileUrl: '', uploadedBy: ''
      });
    } catch (err) {
      console.error(err);
      alert('❌ Failed to upload note');
    }
  };

  return (
    <div className="max-w-2xl mx-auto mt-10 p-6 bg-white dark:bg-gray-900 shadow-lg rounded-lg">
      <h2 className="text-2xl font-bold mb-6 text-blue-700 dark:text-white flex items-center gap-2">
        <FaRegStickyNote />
        Upload Notes
      </h2>

      <form onSubmit={handleSubmit} className="space-y-5">
        {/* University Dropdown */}
        <div>
          <label className="text-gray-700 dark:text-gray-300 text-sm font-semibold flex items-center gap-2">
            <FaUniversity /> University
          </label>
          <select
            name="university"
            onChange={handleChange}
            value={formData.university}
            required
            className="w-full p-2 border border-gray-300 dark:border-gray-600 rounded bg-white dark:bg-gray-800 dark:text-white"
          >
            <option value="">-- Select University --</option>
            {universities.map(u => (
              <option key={u._id} value={u._id}>{u.name}</option>
            ))}
          </select>
        </div>

        {/* Department */}
        <div>
          <label className="text-gray-700 dark:text-gray-300 text-sm font-semibold flex items-center gap-2">
            <FaBook /> Department
          </label>
          <input
            name="departmentName"
            placeholder="e.g. Computer Science"
            value={formData.departmentName}
            onChange={handleChange}
            className="w-full p-2 border rounded dark:bg-gray-800 dark:text-white"
            required
          />
        </div>

        {/* Course */}
        <div>
          <label className="text-gray-700 dark:text-gray-300 text-sm font-semibold flex items-center gap-2">
            <FaBook /> Course Name
          </label>
          <input
            name="courseName"
            placeholder="e.g. BCA"
            value={formData.courseName}
            onChange={handleChange}
            className="w-full p-2 border rounded dark:bg-gray-800 dark:text-white"
            required
          />
        </div>

        {/* Subject */}
        <div>
          <label className="text-gray-700 dark:text-gray-300 text-sm font-semibold flex items-center gap-2">
            <FaKeyboard /> Subject
          </label>
          <input
            name="subject"
            placeholder="e.g. Data Structures"
            value={formData.subject}
            onChange={handleChange}
            className="w-full p-2 border rounded dark:bg-gray-800 dark:text-white"
            required
          />
        </div>

        {/* Title */}
        <div>
          <label className="text-gray-700 dark:text-gray-300 text-sm font-semibold flex items-center gap-2">
            <FaRegStickyNote /> Note Title
          </label>
          <input
            name="title"
            placeholder="e.g. Arrays and Linked Lists"
            value={formData.title}
            onChange={handleChange}
            className="w-full p-2 border rounded dark:bg-gray-800 dark:text-white"
            required
          />
        </div>

        {/* Description */}
        <div>
          <label className="text-gray-700 dark:text-gray-300 text-sm font-semibold flex items-center gap-2">
            <FaInfoCircle /> Description
          </label>
          <textarea
            name="description"
            placeholder="Short description about the notes"
            value={formData.description}
            onChange={handleChange}
            className="w-full p-2 border rounded dark:bg-gray-800 dark:text-white"
          />
        </div>

        {/* File URL */}
        <div>
          <label className="text-gray-700 dark:text-gray-300 text-sm font-semibold flex items-center gap-2">
            <FaFilePdf /> PDF URL
          </label>
          <input
            name="fileUrl"
            placeholder="Paste uploaded PDF URL"
            value={formData.fileUrl}
            onChange={handleChange}
            className="w-full p-2 border rounded dark:bg-gray-800 dark:text-white"
            required
          />
        </div>

        {/* Uploaded By */}
        <div>
          <label className="text-gray-700 dark:text-gray-300 text-sm font-semibold flex items-center gap-2">
            <FaUserEdit /> Uploaded By
          </label>
          <input
            name="uploadedBy"
            placeholder="Your Name"
            value={formData.uploadedBy}
            onChange={handleChange}
            className="w-full p-2 border rounded dark:bg-gray-800 dark:text-white"
          />
        </div>

        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
        >
          Upload Notes
        </button>
      </form>
    </div>
  );
};

export default AddNotes;
