import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Papers = () => {
  const [papers, setPapers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newPaper, setNewPaper] = useState({
    university: '',
    course: '',
    year: '',
    subject: '',
    pdf: null
  });

  // Fetch all papers from backend on load
  useEffect(() => {
    axios.get('http://localhost:5000/api/papers')
      .then((res) => setPapers(res.data))
      .catch((err) => console.error('Failed to load papers:', err));
  }, []);

  const handleDelete = async (id) => {
    try {
      // Optionally implement a DELETE API on the backend
      setPapers(papers.filter((paper) => paper._id !== id));
    } catch (error) {
      console.error('Delete failed:', error);
    }
  };

  const handleAdd = async () => {
    const { university, course, year, subject, pdf } = newPaper;

    if (!university || !course || !year || !subject || !pdf) {
      return alert('Please fill in all fields.');
    }

    const formData = new FormData();
    formData.append('university', university);
    formData.append('course', course);
    formData.append('year', year);
    formData.append('subject', subject);
    formData.append('pdf', pdf);

    try {
      const res = await axios.post('http://localhost:5000/api/papers', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setPapers([...papers, res.data.paper]);
      setNewPaper({
        university: '',
        course: '',
        year: '',
        subject: '',
        pdf: null
      });
      setShowForm(false);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('Upload failed');
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewPaper({ ...newPaper, pdf: file });
    }
  };

  return (
    <div className="p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Manage Question Papers</h2>

      <button
        onClick={() => setShowForm(!showForm)}
        className="mb-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        {showForm ? 'Cancel' : 'Add Paper'}
      </button>

      {showForm && (
        <div className="mb-6 bg-gray-100 p-4 rounded shadow space-y-3">
          <input
            type="text"
            placeholder="University"
            value={newPaper.university}
            onChange={(e) => setNewPaper({ ...newPaper, university: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="text"
            placeholder="Course"
            value={newPaper.course}
            onChange={(e) => setNewPaper({ ...newPaper, course: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="number"
            placeholder="Year"
            value={newPaper.year}
            onChange={(e) => setNewPaper({ ...newPaper, year: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="text"
            placeholder="Subject"
            value={newPaper.subject}
            onChange={(e) => setNewPaper({ ...newPaper, subject: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <div>
            <label className="block mb-1 font-medium">Upload PDF</label>
            <label className="bg-blue-600 text-white px-4 py-2 rounded cursor-pointer hover:bg-blue-700 inline-block">
              Choose File
              <input
                type="file"
                accept="application/pdf"
                onChange={handleFileChange}
                className="hidden"
              />
            </label>
            {newPaper.pdf && <span className="ml-2 text-sm text-gray-700">{newPaper.pdf.name}</span>}
          </div>
          <button
            onClick={handleAdd}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            Submit
          </button>
        </div>
      )}

      <div className="space-y-4">
        {papers.map((paper) => (
          <div key={paper._id} className="border p-4 rounded shadow-sm">
            <h3 className="text-lg font-semibold text-blue-700">{paper.subject} ({paper.year})</h3>
            <p className="text-sm text-gray-600">{paper.course} - {paper.university}</p>
            <a
              href={`http://localhost:5000/uploads/${paper.pdf}`}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-500 text-sm hover:underline"
            >
              View PDF
            </a>
            <div className="mt-2">
              <button
                onClick={() => handleDelete(paper._id)}
                className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600 text-sm"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Papers;
