// src/components/AuthorityNavbar.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import { FaSignOutAlt, FaUserCircle } from 'react-icons/fa';

const AuthorityNavbar = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('authorityToken');
    navigate('/login');
  };

  return (
    <header className="bg-white dark:bg-gray-800 px-6 py-3 shadow-sm flex justify-between items-center border-b dark:border-gray-700">
      <h1 className="text-lg font-semibold text-gray-800 dark:text-white">
        Authority Dashboard
      </h1>

      <div className="flex items-center gap-4">
        <FaUserCircle size={22} className="text-blue-600 dark:text-blue-400" />
        <button
          onClick={handleLogout}
          className="flex items-center gap-2 bg-red-100 dark:bg-red-900 text-red-600 dark:text-red-300 px-3 py-1.5 rounded-lg text-sm hover:bg-red-200 dark:hover:bg-red-800 transition"
        >
          <FaSignOutAlt />
          Logout
        </button>
      </div>
    </header>
  );
};

export default AuthorityNavbar;
