// src/admin-pages/ManageNotes.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTrash, FaEye, FaDownload, FaUniversity, FaBookOpen } from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE
});

const ManageNotes = () => {
  const [notes, setNotes] = useState([]);

  const fetchNotes = async () => {
    try {
      const res = await API.get('/api/resources/notes');
      setNotes(res.data);
    } catch (err) {
      console.error('Failed to fetch notes:', err);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this note?')) return;

    try {
      await API.delete(`/api/resources/${id}`);
      alert('✅ Note deleted');
      fetchNotes();
    } catch (err) {
      console.error('❌ Delete error:', err);
      alert('Failed to delete');
    }
  };

  const handleView = (note) => {
   
    window.open(`${process.env.REACT_APP_API_BASE}${note.fileUrl}`, '_blank');

  };

  const handleDownload = (note) => {
    const downloadUrl = `${process.env.REACT_APP_API_BASE}/api/resources/download/${note._id}`;
    window.open(downloadUrl, '_blank');
  };

  // Group notes by university and department
  const groupedNotes = notes.reduce((acc, note) => {
    const key = `${note.universityName}__${note.department}`;
    if (!acc[key]) acc[key] = [];
    acc[key].push(note);
    return acc;
  }, {});

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <h2 className="text-4xl font-bold mb-8 text-blue-800 flex items-center gap-3">
        <FaBookOpen className="text-green-600" /> Manage Uploaded Notes
      </h2>

      {Object.entries(groupedNotes).length === 0 ? (
        <p className="text-gray-500">No notes found.</p>
      ) : (
        <div className="space-y-10">
          {Object.entries(groupedNotes).map(([groupKey, groupNotes]) => {
            const [university, department] = groupKey.split('__');

            return (
              <div key={groupKey} className="bg-white rounded-xl shadow-md border border-gray-200 p-6">
                <h3 className="text-2xl font-semibold text-blue-700 flex items-center gap-2 mb-1">
                  <FaUniversity className="text-purple-600" /> {university}
                </h3>
                <p className="text-md text-gray-600 mb-5"><strong>Department:</strong> {department}</p>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {groupNotes.map((note) => (
                    <div key={note._id} className="bg-gray-50 rounded-lg p-4 shadow-sm border border-gray-100">
                      <h4 className="text-lg font-medium text-gray-800 mb-2">{note.course} - Semester {note.semester}</h4>

                      <div className="flex justify-between items-center">
                        <button
                          onClick={() => handleView(note)}
                          className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded flex items-center gap-1 text-sm"
                        >
                          <FaEye /> View
                        </button>

                        <button
                          onClick={() => handleDownload(note)}
                          className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded flex items-center gap-1 text-sm"
                        >
                          <FaDownload /> Download
                        </button>

                        <button
                          onClick={() => handleDelete(note._id)}
                          className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded flex items-center gap-1 text-sm"
                        >
                          <FaTrash /> Delete
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default ManageNotes;
