import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div className="p-8 text-center dark:bg-gray-800 dark:text-white bg-gray-100 min-h-screen">
      <h2 className="text-3xl font-bold text-blue-800 mb-4">Welcome to SPU Study Resource Portal</h2>
      <p className="mb-6">Find previous year question papers, syllabus, and notes for all departments.</p>

      <input
        type="text"
        placeholder="Search by subject or code..."
        className="p-2 w-1/2 border rounded mb-4"
      />

      <div className="grid grid-cols-1 sm:grid-cols-3 gap-6 mt-6">
        <div className="bg-blue-100 p-4 rounded shadow">ðŸ“˜ Notes</div>
        <div className="bg-blue-100 p-4 rounded shadow">ðŸ“„ Question Papers</div>
        <div className="bg-blue-100 p-4 rounded shadow">ðŸ“š Syllabus</div>
      </div>

      <div className="mt-10">
        <h2 className="text-2xl font-semibold mb-4">University Details</h2>
        <Link
          to="/university"
          className="text-blue-600 dark:text-blue-400 underline text-lg"
        >
          View All Universities
        </Link>
      </div>
    </div>
  );
};

export default Home;
