import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';

import Home from './Home';
import AboutUs from './AboutUs';
import Teams from './Teams';
import Books from './Books';
import Universities from './Universities';
import CoursePages from './CoursePages';
import CourseDetailsPage from './CourseDetailsPage';

import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import AdminLogin from '../admin-pages/AdminLogin'; // Make sure path is correct

const Index = () => {
  const [showAdminLogin, setShowAdminLogin] = useState(false);

  // Secret keypress: Ctrl + Shift + A
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.ctrlKey && e.shiftKey && e.key.toLowerCase() === 'a') {
        setShowAdminLogin(prev => !prev);
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, []);

  return (
    <>
      <Navbar />
      <main className="flex-grow">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/aboutus" element={<AboutUs />} />
          <Route path="/teams" element={<Teams />} />
          <Route path="/books" element={<Books />} />
          <Route path="/universities" element={<Universities />} />
          <Route path="/coursepages/:id/courses" element={<CoursePages />} />
          <Route path="/course/:universityId/:department/:courseName" element={<CourseDetailsPage />} />
        </Routes>

        {/* Hidden Admin Login - Only appears on shortcut */}
        {showAdminLogin && (
          <div className="fixed top-24 right-6 z-50 shadow-2xl bg-white dark:bg-gray-800 p-6 rounded-xl border border-gray-300 dark:border-gray-700">
            <AdminLogin />
            <button
              onClick={() => setShowAdminLogin(false)}
              className="absolute top-1 right-2 text-sm text-red-500 hover:text-red-700"
            >
              âœ• Close
            </button>
          </div>
        )}
      </main>
      <Footer />
    </>
  );
};

export default Index;
