import React, { useState, useEffect } from 'react';

const Universities = () => {
  const [universities, setUniversities] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newUniversity, setNewUniversity] = useState({
    name: '',
    description: '',
    website: ''
  });

  // Fetch universities from backend on mount
  useEffect(() => {
    fetch('http://localhost:5000/api/universities')
      .then(res => res.json())
      .then(data => setUniversities(data))
      .catch(err => console.error('Failed to fetch universities:', err));
  }, []);

  // Add university
  const handleAdd = async () => {
    if (newUniversity.name && newUniversity.description && newUniversity.website) {
      try {
        const res = await fetch('http://localhost:5000/api/universities', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(newUniversity)
        });

        const data = await res.json();

        if (res.ok) {
          setUniversities([...universities, data.university || data]);
          setNewUniversity({ name: '', description: '', website: '' });
          setShowForm(false);
        } else {
          alert('❌ Failed to add university');
        }
      } catch (err) {
        console.error(err);
        alert('❌ Server error while adding university');
      }
    } else {
      alert('Please fill in all fields.');
    }
  };

  // Delete university
  const handleDelete = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/universities/${id}`, {
        method: 'DELETE'
      });

      if (res.ok) {
        setUniversities(universities.filter(u => u._id !== id));
      } else {
        alert('❌ Failed to delete university');
      }
    } catch (err) {
      console.error(err);
      alert('❌ Server error while deleting university');
    }
  };

  return (
    <div className="p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Manage Universities</h2>

      <button
        onClick={() => setShowForm(!showForm)}
        className="mb-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        {showForm ? 'Cancel' : 'Add University'}
      </button>

      {showForm && (
        <div className="mb-6 bg-gray-100 p-4 rounded shadow space-y-3">
          <input
            type="text"
            placeholder="University Name"
            value={newUniversity.name}
            onChange={(e) => setNewUniversity({ ...newUniversity, name: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <textarea
            placeholder="Description"
            value={newUniversity.description}
            onChange={(e) => setNewUniversity({ ...newUniversity, description: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="url"
            placeholder="Website URL"
            value={newUniversity.website}
            onChange={(e) => setNewUniversity({ ...newUniversity, website: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <button
            onClick={handleAdd}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            Submit
          </button>
        </div>
      )}

      <div className="space-y-4">
        {universities.map((uni) => (
          <div key={uni._id} className="border p-4 rounded shadow-sm">
            <h3 className="text-lg font-semibold text-blue-700">{uni.name}</h3>
            <p className="text-sm text-gray-600">{uni.description}</p>
            <a
              href={uni.website}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-500 text-sm hover:underline"
            >
              Visit Website
            </a>
            <div className="mt-2 flex gap-2">
              <button
                onClick={() => handleDelete(uni._id)}
                className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600 text-sm"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Universities;
