import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaTrash, FaUniversity, FaBookOpen, FaLayerGroup } from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const Courses = () => {
  const [universities, setUniversities] = useState([]);

  useEffect(() => {
    fetchUniversities();
  }, []);

  const fetchUniversities = async () => {
    try {
      const res = await API.get('/api/universities');
      setUniversities(res.data);
    } catch (err) {
      console.error('Failed to fetch universities:', err);
    }
  };

  const handleDelete = async (universityId, departmentName, courseName) => {
    if (!window.confirm('Are you sure you want to delete this course?')) return;
    try {
      await API.patch(`/api/universities/${universityId}/remove-course`, {
        departmentName,
        courseName,
      });
      fetchUniversities(); // Refresh list
    } catch (err) {
      console.error('Failed to delete course:', err);
    }
  };

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <h2 className="text-3xl font-bold mb-8 text-indigo-700 flex items-center gap-3">
        <FaBookOpen className="text-yellow-500" /> All Courses by University
      </h2>

      {universities.length === 0 ? (
        <p className="text-gray-500 text-center mt-10">No universities or courses found.</p>
      ) : (
        universities.map((uni) => (
          <div key={uni._id} className="mb-10">
            {/* University Section */}
            <div className="mb-4 border-b pb-2 border-indigo-300 flex items-center gap-2">
              <FaUniversity className="text-blue-700" />
              <h3 className="text-2xl font-semibold text-blue-700">{uni.name}</h3>
            </div>

            {/* Departments */}
            {uni.departments?.length > 0 ? (
              uni.departments.map((dept, deptIndex) => (
                <div key={deptIndex} className="mb-6 ml-4">
                  {/* Department Heading */}
                  <div className="flex items-center gap-2 mb-2">
                    <FaLayerGroup className="text-gray-700" />
                    <h4 className="text-xl font-medium text-gray-800">
                      Department of {dept.name}
                    </h4>
                  </div>

                  {/* Courses Grid */}
                  <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6 mt-3">
                    {dept.courses?.length > 0 ? (
                      dept.courses.map((course, cIndex) => (
                        <div
                          key={cIndex}
                          className="bg-white border border-gray-200 p-4 rounded-xl shadow hover:shadow-lg transition"
                        >
                          <h5 className="text-lg font-semibold text-indigo-800 mb-1">{course.name}</h5>
                          <p className="text-sm text-gray-600 mb-1">Level: {course.level}</p>
                          <p className="text-sm text-gray-600 mb-1">Duration: {course.duration} years</p>
                          <p className="text-sm text-gray-600 mb-3">Semesters: {course.semesters}</p>

                          <button
                            onClick={() =>
                              handleDelete(uni._id, dept.name, course.name)
                            }
                            className="inline-flex items-center gap-2 text-sm bg-red-500 hover:bg-red-600 text-white px-3 py-1.5 rounded"
                          >
                            <FaTrash /> Delete
                          </button>
                        </div>
                      ))
                    ) : (
                      <p className="text-gray-500 col-span-full italic">No courses available in this department.</p>
                    )}
                  </div>
                </div>
              ))
            ) : (
              <p className="text-gray-500 italic ml-4">No departments found in this university.</p>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default Courses;
