// src/components/UserSidebar.js
import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import {
  FaBars,
  FaTimes,
  FaTachometerAlt,
  FaBookOpen,
  FaUniversity,
  FaSignOutAlt
} from "react-icons/fa";

const UserSidebar = ({ children }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => setIsOpen(!isOpen);

  const linkClass =
    "flex items-center gap-3 px-4 py-2 rounded-md hover:bg-blue-500 hover:text-white transition-colors";
  const activeLinkClass = "bg-blue-600 text-white";

  return (
    <div className="flex min-h-screen bg-gray-50 dark:bg-gray-900">
      {/* Sidebar */}
      <aside
        className={`fixed top-0 left-0 h-full bg-white dark:bg-gray-800 shadow-lg z-50 transform ${
          isOpen ? "translate-x-0" : "-translate-x-full"
        } transition-transform duration-300 md:translate-x-0 md:static md:w-64`}
      >
        {/* Brand */}
        <div className="p-4 border-b border-gray-200 dark:border-gray-700 flex justify-between items-center md:block">
          <h2 className="text-2xl font-semibold text-gray-900 dark:text-white">
            Academic Source
          </h2>
          {/* Close Button (Mobile) */}
          <button
            onClick={toggleSidebar}
            className="md:hidden text-2xl text-gray-800 dark:text-white"
          >
            <FaTimes />
          </button>
        </div>

        {/* Nav Links */}
        <nav className="flex flex-col gap-2 p-4 text-gray-700 dark:text-gray-200">
          <NavLink
            to="/user"
            end
            className={({ isActive }) =>
              `${linkClass} ${isActive ? activeLinkClass : ""}`
            }
            onClick={() => setIsOpen(false)}
          >
            <FaTachometerAlt /> Dashboard
          </NavLink>
          <NavLink
            to="/user/resources"
            className={({ isActive }) =>
              `${linkClass} ${isActive ? activeLinkClass : ""}`
            }
            onClick={() => setIsOpen(false)}
          >
            <FaBookOpen /> My Resources
          </NavLink>
          <NavLink
            to="/user/university"
            className={({ isActive }) =>
              `${linkClass} ${isActive ? activeLinkClass : ""}`
            }
            onClick={() => setIsOpen(false)}
          >
            <FaUniversity /> Explore University
          </NavLink>
          <NavLink
            to="/logout"
            className={({ isActive }) =>
              `${linkClass} ${isActive ? activeLinkClass : ""}`
            }
            onClick={() => setIsOpen(false)}
          >
            <FaSignOutAlt /> Logout
          </NavLink>
        </nav>
      </aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Top Navbar (Mobile) */}
        <header className="md:hidden bg-white dark:bg-gray-900 p-4 shadow flex justify-between items-center">
          <h2 className="text-xl font-bold text-gray-800 dark:text-white">
            User Panel
          </h2>
          <button
            onClick={toggleSidebar}
            className="text-2xl text-gray-800 dark:text-white"
          >
            <FaBars />
          </button>
        </header>

        {/* Content Area */}
        <main className="p-4 md:p-6 overflow-auto">{children}</main>
      </div>
    </div>
  );
};

export default UserSidebar;
