import React, { useEffect, useState } from 'react';
import api from 'axios'; // Axios instance with baseURL
import { FaEdit, FaTrash } from 'react-icons/fa';

const AdminDepartments = () => {
  const [universityId, setUniversityId] = useState('');
  const [departments, setDepartments] = useState([]);
  const [editingIndex, setEditingIndex] = useState(null);
  const [newName, setNewName] = useState('');

  // Fetch departments on mount
  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const res = await api.get(`/universities/${universityId}`);
        setDepartments(res.data.departments);
      } catch (err) {
        console.error('Error fetching departments:', err);
      }
    };

    if (universityId) fetchDepartments();
  }, [universityId]);

  const handleEdit = (index) => {
    setEditingIndex(index);
    setNewName(departments[index].name);
  };

  const handleUpdate = async (deptId) => {
    try {
      await api.put(`/universities/${universityId}/departments/${deptId}`, {
        name: newName,
      });
      const updated = [...departments];
      updated[editingIndex].name = newName;
      setDepartments(updated);
      setEditingIndex(null);
    } catch (err) {
      console.error('Update failed:', err);
    }
  };

  const handleDelete = async (deptId) => {
    try {
      await api.delete(`/universities/${universityId}/departments/${deptId}`);
      setDepartments(departments.filter((d) => d._id !== deptId));
    } catch (err) {
      console.error('Delete failed:', err);
    }
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Manage Departments</h2>

      {/* University ID Input (can be dropdown in real case) */}
      <div className="mb-4">
        <input
          type="text"
          placeholder="Enter University ID"
          value={universityId}
          onChange={(e) => setUniversityId(e.target.value)}
          className="border p-2 w-full"
        />
      </div>

      {departments.map((dept, index) => (
        <div
          key={dept._id}
          className="flex items-center justify-between bg-white shadow-md p-3 mb-2 rounded"
        >
          {editingIndex === index ? (
            <input
              type="text"
              value={newName}
              onChange={(e) => setNewName(e.target.value)}
              className="border p-1 flex-grow mr-2"
            />
          ) : (
            <p>{dept.name}</p>
          )}

          <div className="space-x-2">
            {editingIndex === index ? (
              <button
                onClick={() => handleUpdate(dept._id)}
                className="bg-green-600 text-white px-2 py-1 rounded"
              >
                Save
              </button>
            ) : (
              <button
                onClick={() => handleEdit(index)}
                className="text-blue-600"
              >
                <FaEdit />
              </button>
            )}
            <button
              onClick={() => handleDelete(dept._id)}
              className="text-red-600"
            >
              <FaTrash />
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default AdminDepartments;
