import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Menu, X, Sun, Moon } from 'lucide-react'; // lucide-react for icons (optional)
import ARMS from './assets/ARMS.png'
const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const location = useLocation();

  useEffect(() => {
    // close mobile menu on route change
    setMenuOpen(false);
  }, [location]);

  // toggle light/dark mode
  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  return (
    <header className="bg-white dark:bg-gray-900 text-gray-800 dark:text-white shadow-md sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16 items-center">
          {/* Brand */}
         <Link to="/">
  <img src={ARMS} alt="ARMS Portal Logo" className="h-10 w-auto" />
</Link>

          {/* Desktop Links */}
          <nav className="hidden md:flex space-x-6 items-center">
            <Link to="/" className="hover:text-blue-500">Home</Link>
            <Link to="/aboutus" className="hover:text-blue-500">About Us</Link>
              <Link to="/universities" className="block hover:text-blue-500">Universities</Link>
              <Link to="/Teams" className="block hover:text-blue-500"> Our Teams</Link>
            <Link to="/admin/login" className="hover:text-blue-500">Admin Login</Link>
            <button
              onClick={() => setDarkMode(!darkMode)}
              className="ml-2 p-2 rounded-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600"
              title="Toggle reading mode"
            >
              {darkMode ? <Sun size={18} /> : <Moon size={18} />}
            </button>
          </nav>

          {/* Mobile menu button */}
          <div className="md:hidden flex items-center">
            <button onClick={() => setMenuOpen(!menuOpen)} className="p-2 rounded-md hover:bg-gray-100 dark:hover:bg-gray-700">
              {menuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Menu */}
      {menuOpen && (
        <div className="md:hidden bg-white dark:bg-gray-900 px-4 pb-4 space-y-3">
          <Link to="/" className="block hover:text-blue-500">Home</Link>
          <Link to="/aboutus" className="block hover:text-blue-500">About Us</Link>
            <Link to="/universities" className="block hover:text-blue-500">Universities</Link>
              <Link to="/Teams" className="block hover:text-blue-500"> Our Teams</Link>
          <Link to="/admin/login" className="block hover:text-blue-500">Admin Login</Link>
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="block w-full text-left p-2 rounded bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600"
          >
            {darkMode ? 'Light Mode' : 'Dark Mode'}
          </button>
        </div>
      )}
    </header>
  );
};

export default Navbar;
