import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faBars,
  faMoon,
  faSun
} from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';

export default function Navbar() {
  const [darkMode, setDarkMode] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleDarkMode = () => setDarkMode(!darkMode);
  const toggleMenu = () => setMenuOpen(!menuOpen);

  useEffect(() => {
    const root = window.document.documentElement;
    darkMode ? root.classList.add('dark') : root.classList.remove('dark');
  }, [darkMode]);

  return (
    <>
      {/* Top Navbar */}
      <header className="sticky top-0 bg-white dark:bg-gray-900 text-black dark:text-white p-3 shadow flex justify-between items-center z-40">
        <h1 className="text-lg font-semibold">ARMS</h1>

        <button onClick={toggleMenu} className="sm:hidden text-xl">
          <FontAwesomeIcon icon={faBars} />
        </button>

        <nav className="hidden sm:flex gap-4">
          <Link to="/" className="hover:underline">Home</Link>
          <Link to="/departments" className="hover:underline">Departments</Link>
          <Link to="/university" className="hover:underline">University</Link>
          <Link to="/contact" className="hover:underline">Contact Us</Link>
          <Link to="/members" className="hover:underline">Special Members</Link>
          <Link to="/admin" className="hover:underline">Admin Login</Link>
        </nav>

        <button onClick={toggleDarkMode} className="text-xl ml-4">
          <FontAwesomeIcon icon={darkMode ? faSun : faMoon} />
        </button>
      </header>

      {/* Mobile Menu */}
      {menuOpen && (
        <div className="sm:hidden bg-white dark:bg-gray-900 text-black dark:text-white shadow p-4 flex flex-col space-y-3">
          <Link to="/" className="hover:underline" onClick={toggleMenu}>Home</Link>
          <Link to="/departments" className="hover:underline" onClick={toggleMenu}>Departments</Link>
          <Link to="/university" className="hover:underline" onClick={toggleMenu}>University</Link>
          <Link to="/contact" className="hover:underline" onClick={toggleMenu}>Contact Us</Link>
          <Link to="/members" className="hover:underline" onClick={toggleMenu}>Special Members</Link>
          <Link to="/admin" className="hover:underline" onClick={toggleMenu}>Admin Login</Link>
        </div>
      )}

      {/* Main Content Placeholder */}
      <main className="p-4 dark:bg-gray-800 dark:text-white bg-gray-100 min-h-screen">
        <p>Welcome to ARMS Portal!</p>
      </main>
    </>
  );
}
