// src/admin-pages/ManageNotes.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  FaTrash,
  FaEye,
  FaDownload,
  FaUniversity,
  FaBookOpen
} from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const ManageNotes = () => {
  const [notes, setNotes] = useState([]);

  const fetchNotes = async () => {
    try {
      const res = await API.get('/api/resources/notes');
      setNotes(res.data);
    } catch (err) {
      console.error('Failed to fetch notes:', err);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this note?')) return;
    try {
      await API.delete(`/api/resources/${id}`);
      alert('✅ Note deleted successfully.');
      fetchNotes();
    } catch (err) {
      console.error('❌ Delete failed:', err);
      alert('❌ Failed to delete note.');
    }
  };

  const handleView = (note) => {
    const fullUrl = `${process.env.REACT_APP_API_BASE}${note.fileUrl}`;
    window.open(fullUrl, '_blank', 'noopener,noreferrer');
  };

  const handleDownload = (note) => {
    const downloadUrl = `${process.env.REACT_APP_API_BASE}/api/resources/download/${note._id}`;
    window.open(downloadUrl, '_blank', 'noopener,noreferrer');
  };

  // Group notes by university and department
  const groupedByUniversity = notes.reduce((acc, note) => {
    if (!acc[note.universityName]) acc[note.universityName] = {};
    if (!acc[note.universityName][note.department]) acc[note.universityName][note.department] = [];
    acc[note.universityName][note.department].push(note);
    return acc;
  }, {});

  return (
    <div className="p-8 bg-gradient-to-b from-blue-50 to-white min-h-screen">
      <h2 className="text-4xl font-bold text-center text-blue-800 mb-12 flex justify-center items-center gap-3">
        <FaBookOpen className="text-green-600" /> Manage Uploaded Notes
      </h2>

      {Object.entries(groupedByUniversity).length === 0 ? (
        <p className="text-center text-gray-500 text-lg">No notes available.</p>
      ) : (
        Object.entries(groupedByUniversity).map(([universityName, departments]) => (
          <section key={universityName} className="mb-16 bg-white shadow-xl rounded-xl p-8 border">
            <h3 className="text-3xl font-semibold text-center text-purple-700 mb-8 flex justify-center items-center gap-2">
              <FaUniversity className="text-purple-600" />
              {universityName}
            </h3>

            {Object.entries(departments).map(([department, deptNotes]) => (
              <div key={department} className="mb-12">
                <h4 className="text-2xl text-center font-semibold text-blue-700 mb-6 border-b pb-2">
                  Department of {department}
                </h4>

                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                  {deptNotes.map((note) => (
                    <div key={note._id} className="bg-gray-50 hover:shadow-lg transition-shadow duration-300 border rounded-xl p-6 text-center flex flex-col justify-between h-full">
                      <div>
                        <h5 className="text-xl font-semibold text-gray-800 mb-2">
                          {note.course}
                        </h5>
                        <p className="text-sm text-gray-600 mb-2">
                          <strong>Semester:</strong> {note.semester}
                        </p>
                        <p className="text-sm text-gray-500 mb-4">
                          <strong>Note Type:</strong> Lecture / Exam / Others
                        </p>
                      </div>

                      <div className="flex justify-center gap-2 mt-auto">
                        <button
                          onClick={() => handleView(note)}
                          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md flex items-center gap-2 text-sm"
                        >
                          <FaEye /> View
                        </button>

                        <button
                          onClick={() => handleDownload(note)}
                          className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-md flex items-center gap-2 text-sm"
                        >
                          <FaDownload /> Download
                        </button>

                        <button
                          onClick={() => handleDelete(note._id)}
                          className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-md flex items-center gap-2 text-sm"
                        >
                          <FaTrash /> Delete
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </section>
        ))
      )}
    </div>
  );
};

export default ManageNotes;
