import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaEdit, FaTrash } from 'react-icons/fa';

const BASE_URL = 'http://localhost:5000';

const AdminDepartments = () => {
  const [universities, setUniversities] = useState([]);
  const [selectedUniversityId, setSelectedUniversityId] = useState('');
  const [departments, setDepartments] = useState([]);
  const [editingIndex, setEditingIndex] = useState(null);
  const [newName, setNewName] = useState('');

  // Fetch universities
  useEffect(() => {
    const fetchUniversities = async () => {
      try {
        const res = await axios.get(`${BASE_URL}/api/universities`);
        setUniversities(res.data);
      } catch (error) {
        console.error('Error fetching universities:', error);
      }
    };

    fetchUniversities();
  }, []);

  // Update departments when selectedUniversityId changes
  useEffect(() => {
    if (!selectedUniversityId) {
      setDepartments([]);
      return;
    }

    const selected = universities.find((u) => u._id === selectedUniversityId);
    if (selected) {
      setDepartments(selected.departments || []);
    }
  }, [selectedUniversityId, universities]);

  const handleEdit = (index) => {
    setEditingIndex(index);
    setNewName(departments[index].name);
  };

  const handleUpdate = async (deptId) => {
    try {
      await axios.put(
        `${BASE_URL}/api/universities/${selectedUniversityId}/departments/${deptId}`,
        { name: newName }
      );

      const updated = [...departments];
      updated[editingIndex].name = newName;
      setDepartments(updated);
      setEditingIndex(null);
      alert('✅ Department updated');
    } catch (err) {
      console.error('Update failed:', err);
      alert('❌ Could not update department');
    }
  };

  const handleDelete = async (deptId) => {
    try {
      await axios.delete(
        `${BASE_URL}/api/universities/${selectedUniversityId}/departments/${deptId}`
      );
      const filtered = departments.filter((d) => d._id !== deptId);
      setDepartments(filtered);
      alert('✅ Department deleted');
    } catch (err) {
      console.error('Delete failed:', err);
      alert('❌ Could not delete department');
    }
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Edit or Delete Departments</h2>

      {/* University Dropdown */}
      <select
        value={selectedUniversityId}
        onChange={(e) => setSelectedUniversityId(e.target.value)}
        className="border border-gray-300 p-2 rounded w-full mb-4"
      >
        <option value="">-- Select University --</option>
        {universities.map((uni) => (
          <option key={uni._id} value={uni._id}>
            {uni.name}
          </option>
        ))}
      </select>

      {/* Departments List */}
      {selectedUniversityId && departments.length === 0 && (
        <p className="text-gray-500">No departments found.</p>
      )}

      {departments.map((dept, index) => (
        <div
          key={dept._id}
          className="flex items-center justify-between bg-white p-3 shadow rounded mb-2"
        >
          {editingIndex === index ? (
            <input
              type="text"
              value={newName}
              onChange={(e) => setNewName(e.target.value)}
              className="border p-1 flex-grow mr-2"
            />
          ) : (
            <span className="flex-grow">{dept.name}</span>
          )}
          <div className="flex space-x-2">
            {editingIndex === index ? (
              <button
                onClick={() => handleUpdate(dept._id)}
                className="bg-green-500 text-white px-2 py-1 rounded"
              >
                Save
              </button>
            ) : (
              <button
                onClick={() => handleEdit(index)}
                className="text-blue-600 hover:text-blue-800"
              >
                <FaEdit />
              </button>
            )}
            <button
              onClick={() => handleDelete(dept._id)}
              className="text-red-600 hover:text-red-800"
            >
              <FaTrash />
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default AdminDepartments;
