import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTrash } from 'react-icons/fa';

const ManageNotes = () => {
  const [notes, setNotes] = useState([]);

  const fetchNotes = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_BASE}/resources/notes`);
      setNotes(res.data);
    } catch (err) {
      console.error('Error fetching notes:', err);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this note?')) return;
    try {
      await axios.delete(`${process.env.REACT_APP_API_BASE}/resources/${id}`);
      setNotes(notes.filter((note) => note._id !== id));
    } catch (err) {
      console.error('Delete failed:', err);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Manage Notes</h2>
      <ul className="space-y-2">
        {notes.map((note) => (
          <li key={note._id} className="bg-white shadow p-3 rounded flex justify-between items-center">
            <span>{note.title}</span>
            <button onClick={() => handleDelete(note._id)} className="text-red-500 hover:text-red-700">
              <FaTrash />
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageNotes;
