import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UploadResource = () => {
  const [universities, setUniversities] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [courses, setCourses] = useState([]);
  const [semesters, setSemesters] = useState([]);

  const [formData, setFormData] = useState({
    university: '',
    department: '',
    course: '',
    semester: '',
    type: 'notes',
    title: '',
    file: null,
  });

  useEffect(() => {
    axios.get('/api/universities')
      .then(res => setUniversities(res.data))
      .catch(err => console.error('Error fetching universities:', err));
  }, []);

  useEffect(() => {
    if (formData.university) {
      axios.get(`/api/departments?universityId=${formData.university}`)
        .then(res => setDepartments(res.data))
        .catch(err => console.error('Error fetching departments:', err));
    } else {
      setDepartments([]);
    }
    setFormData(prev => ({ ...prev, department: '', course: '', semester: '' }));
  }, [formData.university]);

  useEffect(() => {
    if (formData.department) {
      axios.get(`/api/courses?departmentId=${formData.department}`)
        .then(res => setCourses(res.data))
        .catch(err => console.error('Error fetching courses:', err));
    } else {
      setCourses([]);
    }
    setFormData(prev => ({ ...prev, course: '', semester: '' }));
  }, [formData.department]);

  useEffect(() => {
    if (formData.course) {
      axios.get(`/api/semesters?courseId=${formData.course}`)
        .then(res => setSemesters(res.data))
        .catch(err => console.error('Error fetching semesters:', err));
    } else {
      setSemesters([]);
    }
    setFormData(prev => ({ ...prev, semester: '' }));
  }, [formData.course]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    setFormData(prev => ({ ...prev, file: e.target.files[0] }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    for (let key in formData) {
      data.append(key, formData[key]);
    }

    try {
      await axios.post('/api/resources/upload', data);
      alert('✅ Resource uploaded successfully!');
    } catch (err) {
      console.error('Upload failed:', err);
      alert('❌ Failed to upload resource.');
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white shadow-lg rounded-lg mt-8">
      <h2 className="text-2xl font-bold text-center text-indigo-700 mb-6">Upload Academic Resource</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data" className="space-y-4">

        {/* University */}
        <div>
          <label className="block font-semibold mb-1">University</label>
          <select
            name="university"
            value={formData.university}
            onChange={handleChange}
            className="w-full p-2 border border-gray-300 rounded"
            required
          >
            <option value="">Select University</option>
            {universities.map(uni => (
              <option key={uni._id} value={uni._id}>{uni.name}</option>
            ))}
          </select>
        </div>

        {/* Department */}
        <div>
          <label className="block font-semibold mb-1">Department</label>
          <select
            name="department"
            value={formData.department}
            onChange={handleChange}
            className="w-full p-2 border border-gray-300 rounded"
            required
          >
            <option value="">Select Department</option>
            {departments.map(dept => (
              <option key={dept._id} value={dept._id}>{dept.name}</option>
            ))}
          </select>
        </div>

        {/* Course */}
        <div>
          <label className="block font-semibold mb-1">Course</label>
          <select
            name="course"
            value={formData.course}
            onChange={handleChange}
            className="w-full p-2 border border-gray-300 rounded"
            required
          >
            <option value="">Select Course</option>
            {courses.map(course => (
              <option key={course._id} value={course._id}>{course.name}</option>
            ))}
          </select>
        </div>

        {/* Semester */}
        <div>
          <label className="block font-semibold mb-1">Semester</label>
          <select
            name="semester"
            value={formData.semester}
            onChange={handleChange}
            className="w-full p-2 border border-gray-300 rounded"
            required
          >
            <option value="">Select Semester</option>
            {semesters.map(sem => (
              <option key={sem._id} value={sem._id}>{sem.name}</option>
            ))}
          </select>
        </div>

        {/* Title */}
        <div>
          <label className="block font-semibold mb-1">Title</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            placeholder="e.g., Data Structure Notes"
            className="w-full p-2 border border-gray-300 rounded"
            required
          />
        </div>

        {/* Type */}
        <div>
          <label className="block font-semibold mb-1">Type</label>
          <select
            name="type"
            value={formData.type}
            onChange={handleChange}
            className="w-full p-2 border border-gray-300 rounded"
            required
          >
            <option value="notes">Notes</option>
            <option value="syllabus">Syllabus</option>
            <option value="papers">Papers</option>
          </select>
        </div>

        {/* File */}
        <div>
          <label className="block font-semibold mb-1">Upload File</label>
          <input
            type="file"
            onChange={handleFileChange}
            accept=".pdf,.doc,.docx,.ppt,.pptx,.jpg,.jpeg,.png"
            className="w-full p-2 border border-gray-300 rounded bg-white"
            required
          />
        </div>

        {/* Submit */}
        <button
          type="submit"
          className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-4 rounded"
        >
          Upload Resource
        </button>
      </form>
    </div>
  );
};

export default UploadResource;
