import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { FaChalkboardTeacher } from 'react-icons/fa';

const CoursePages = () => {
  const { universityId } = useParams();
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/courses/university/${universityId}`)
      .then((res) => setCourses(res.data))
      .catch((err) => console.error('Failed to fetch courses:', err));
  }, [universityId]);

  return (
    <div className="min-h-screen px-6 py-16 bg-gray-50 dark:bg-gray-900 text-gray-800 dark:text-white">
      <h1 className="text-4xl sm:text-5xl font-extrabold text-center mb-12 text-green-800 dark:text-green-300 flex justify-center items-center gap-3">
        <FaChalkboardTeacher className="text-yellow-400 text-5xl" />
        <span>Courses Offered</span>
      </h1>

      {courses.length === 0 ? (
        <p className="text-center text-gray-500 dark:text-gray-400">
          No courses found for this university.
        </p>
      ) : (
        <div className="grid sm:grid-cols-2 md:grid-cols-3 gap-8 max-w-6xl mx-auto">
          {courses.map((course, i) => (
            <div
              key={i}
              className="bg-white dark:bg-gray-800 shadow-md rounded-xl p-6 border border-green-100 dark:border-gray-700"
            >
              <h2 className="text-xl font-semibold text-green-700 dark:text-green-200 mb-2">
                {course.courseName}
              </h2>
              <p className="text-sm text-gray-600 dark:text-gray-400">
                <strong>Semester:</strong> {course.semester}
              </p>
              <p className="text-sm text-gray-600 dark:text-gray-400">
                <strong>Department:</strong> {course.department}
              </p>
              <p className="text-sm text-gray-600 dark:text-gray-400">
                <strong>University:</strong>{' '}
                {course.university?.name || 'N/A'}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default CoursePages;
