import React from 'react';
import { Link } from 'react-router-dom';

const departments = [
  'Computer Science',
  'Electrical',
  'Mechanical',
  'Civil',
  'Electronics & Communication',
  'Information Technology',
];

const Departments = () => {
  return (
    <div className="p-8">
      <h2 className="text-2xl font-bold text-blue-800 mb-4">Departments</h2>
      <div className="grid grid-cols-2 gap-6">
        {departments.map((dept) => (
          <Link to={`/departments/${dept.toLowerCase().replace(/\s+/g, '-')}`} key={dept}>
            <div className="bg-blue-100 p-4 rounded shadow hover:bg-blue-200 transition">
              {dept}
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Departments;
