// src/admin-pages/UploadResources.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UploadResources = () => {
  const [universities, setUniversities] = useState([]);
  const [formData, setFormData] = useState({
    university: '',
    department: '',
    course: '',
    semester: '',
    type: 'notes', // notes | papers | syllabus
    title: '',
    file: null,
  });

  useEffect(() => {
    axios.get('http://localhost:5000/api/universities')
      .then(res => setUniversities(res.data))
      .catch(err => console.error('Failed to fetch universities:', err));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    setFormData(prev => ({ ...prev, file: e.target.files[0] }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    Object.keys(formData).forEach(key => {
      if (key !== 'file') data.append(key, formData[key]);
    });
    data.append('file', formData.file);

    try {
      await axios.post(`http://localhost:5000/api/resources/${formData.type}`, data);
      alert('‚úÖ Uploaded successfully');
      setFormData({
        university: '',
        department: '',
        course: '',
        semester: '',
        type: 'notes',
        title: '',
        file: null,
      });
    } catch (err) {
      console.error('Upload error:', err);
      alert('‚ùå Failed to upload resource');
    }
  };

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-6 text-blue-700">üìö Upload Academic Resource</h2>
      <form onSubmit={handleSubmit} className="space-y-4">

        <select name="university" value={formData.university} onChange={handleChange} className="w-full border p-2 rounded" required>
          <option value="">Select University</option>
          {universities.map(u => <option key={u._id} value={u._id}>{u.name}</option>)}
        </select>

        <input type="text" name="department" value={formData.department} onChange={handleChange} className="w-full border p-2 rounded" placeholder="Department (e.g., Computer Science)" required />

        <input type="text" name="course" value={formData.course} onChange={handleChange} className="w-full border p-2 rounded" placeholder="Course (e.g., B.Tech CSE)" required />

        <input type="number" name="semester" value={formData.semester} onChange={handleChange} className="w-full border p-2 rounded" placeholder="Semester Number" required />

        <select name="type" value={formData.type} onChange={handleChange} className="w-full border p-2 rounded">
          <option value="notes">Notes</option>
          <option value="papers">Question Paper</option>
          <option value="syllabus">Syllabus</option>
        </select>

        <input type="text" name="title" value={formData.title} onChange={handleChange} className="w-full border p-2 rounded" placeholder="Title / Subject" required />

        <input type="file" accept="application/pdf" onChange={handleFileChange} className="w-full border p-2 rounded" required />

        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">Upload</button>
      </form>
    </div>
  );
};

export default UploadResources;
