import React from 'react';
import { Link } from 'react-router-dom';

const departmentCourses = [
  {
    course: 'Undergraduate (UG)',
    departments: [
      'Computer Science',
      'Electrical Engineering',
      'Mechanical Engineering'
    ]
  },
  {
    course: 'Postgraduate (PG)',
    departments: [
      'Computer Applications',
      'Power Systems',
      'Thermal Engineering'
    ]
  },
  {
    course: 'PhD',
    departments: [
      'AI Research',
      'VLSI Design',
      'Mechanical Dynamics'
    ]
  }
];

const Departments = () => {
  return (
    <div className="p-8">
      <h2 className="text-2xl font-bold text-blue-800 mb-4">Departments by Course</h2>
      {departmentCourses.map((group, idx) => (
        <div key={idx} className="mb-6">
          <h3 className="text-xl font-semibold text-green-700 mb-3">{group.course}</h3>
          <div className="grid grid-cols-2 gap-4">
            {group.departments.map((dept) => (
              <Link
                key={dept}
                to={`/departments/${dept.toLowerCase().replace(/\s+/g, '-')}`}
              >
                <div className="bg-blue-100 p-4 rounded shadow hover:bg-blue-200 transition">
                  {dept}
                </div>
              </Link>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Departments;
