// src/admin-pages/AdminTrusted.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  FaTrash,
  FaUserShield,
  FaPlusCircle,
  FaUserCheck,
  FaUserTag,
  FaEnvelope,
} from 'react-icons/fa';

const API = axios.create({
  baseURL: 'http://localhost:5000/api', // adjust if deployed
});

const AdminTrusted = () => {
  const [trustedPersons, setTrustedPersons] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    position: '',
    description: '',
    email: '',
    image: null,
  });

  const fetchTrustedPersons = async () => {
    try {
      const res = await API.get('/trusted');
      setTrustedPersons(res.data);
    } catch (error) {
      console.error('Error fetching trusted persons:', error);
    }
  };

  useEffect(() => {
    fetchTrustedPersons();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const data = new FormData();
      Object.entries(formData).forEach(([key, value]) => data.append(key, value));

      await API.post('/trusted', data);
      fetchTrustedPersons();

      setFormData({
        name: '',
        position: '',
        description: '',
        email: '',
        image: null,
      });
    } catch (error) {
      console.error('Upload failed:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await API.delete(`/trusted/${id}`);
      fetchTrustedPersons();
    } catch (error) {
      console.error('Delete failed:', error);
    }
  };

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h2 className="text-2xl font-semibold mb-6 flex items-center gap-3 text-blue-700">
        <FaUserShield className="text-xl" /> Manage Trusted Persons
      </h2>

      <form
        onSubmit={handleSubmit}
        className="bg-white shadow-md rounded-lg p-6 grid gap-4 md:grid-cols-2 mb-10"
      >
        <input
          type="text"
          placeholder="Name"
          value={formData.name}
          required
          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          className="p-3 border rounded w-full"
        />
        <input
          type="text"
          placeholder="Position"
          value={formData.position}
          required
          onChange={(e) => setFormData({ ...formData, position: e.target.value })}
          className="p-3 border rounded w-full"
        />
        <input
          type="email"
          placeholder="Email"
          value={formData.email}
          required
          onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          className="p-3 border rounded w-full"
        />
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setFormData({ ...formData, image: e.target.files[0] })}
          required
          className="p-3 border rounded w-full"
        />
        <textarea
          placeholder="Description (optional)"
          value={formData.description}
          onChange={(e) => setFormData({ ...formData, description: e.target.value })}
          className="p-3 border rounded w-full md:col-span-2"
        />
        <button
          type="submit"
          className="bg-blue-600 hover:bg-blue-700 text-white py-3 px-6 rounded flex items-center gap-2 md:col-span-2 justify-center"
        >
          <FaPlusCircle /> Add Trusted Person
        </button>
      </form>

      {/* Cards Grid */}
      <div className="grid gap-6 sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-3">
        {trustedPersons.map((person) => (
          <div
            key={person._id}
            className="bg-white shadow-lg rounded-xl overflow-hidden transition-transform hover:scale-[1.02]"
          >
            <div className="p-5 flex flex-col items-center text-center">
              <img
                src={`http://localhost:5000${person.image}`}
                alt={person.name}
                className="w-24 h-24 object-cover rounded-full border-4 border-blue-600 mb-3"
              />
              <h3 className="text-lg font-semibold text-gray-800 flex items-center gap-2">
                <FaUserCheck className="text-green-600" /> {person.name}
              </h3>
              <p className="text-sm text-gray-600 flex items-center gap-2 mt-1">
                <FaUserTag className="text-gray-500" /> {person.position}
              </p>
              <p className="text-sm text-gray-600 flex items-center gap-2 mt-1">
                <FaEnvelope className="text-gray-500" /> {person.email}
              </p>
              {person.description && (
                <p className="text-xs text-gray-500 mt-2">{person.description}</p>
              )}
            </div>
            <div className="bg-gray-100 p-3 text-right">
              <button
                onClick={() => handleDelete(person._id)}
                className="text-red-600 hover:text-red-800 flex items-center gap-1 text-sm"
              >
                <FaTrash /> Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminTrusted;
