import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  FaTrash,
  FaUserShield,
  FaPlusCircle,
  FaEnvelope,
  FaBriefcase,
  FaUser,
  FaRegFileAlt,
} from 'react-icons/fa';

// API Setup
const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE || 'http://localhost:5000/api',
});

const AdminTrusted = () => {
  const [trustedPersons, setTrustedPersons] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    position: '',
    description: '',
    email: '',
    image: null,
  });
  const [imagePreview, setImagePreview] = useState(null);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Fetch Data
  const fetchTrustedPersons = async () => {
    try {
      const response = await API.get('/api/trusted');
      setTrustedPersons(response.data);
    } catch (err) {
      console.error('Error fetching trusted persons:', err);
    }
  };

  useEffect(() => {
    fetchTrustedPersons();
  }, []);

  // Delete Handler
  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure to delete this person?')) return;
    try {
      await API.delete(`/trusted/${id}`);
      setMessage('Deleted successfully');
      fetchTrustedPersons();
    } catch (err) {
      setError('Delete failed');
      console.error(err);
    }
  };

  // Submit Handler
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setMessage('');
    setLoading(true);

    if (!formData.image) {
      setError('Please upload an image');
      setLoading(false);
      return;
    }

    const data = new FormData();
    for (const key in formData) {
      data.append(key, formData[key]);
    }

    try {
      await API.post('/trusted', data);
      setMessage('Trusted person added successfully');
      setFormData({
        name: '',
        position: '',
        description: '',
        email: '',
        image: null,
      });
      setImagePreview(null);
      fetchTrustedPersons();
    } catch (err) {
      console.error('Upload failed:', err);
      setError(err.response?.data?.error || 'Upload failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6">
      {/* Heading */}
      <div className="flex items-center gap-3 mb-6">
        <FaUserShield className="text-2xl text-blue-600" />
        <h2 className="text-2xl font-bold text-gray-800">
          Trusted Persons Management
        </h2>
      </div>

      {/* Status Messages */}
      {message && <p className="text-green-600 mb-3">{message}</p>}
      {error && <p className="text-red-600 mb-3">{error}</p>}

      {/* Form */}
      <form
        onSubmit={handleSubmit}
        className="bg-gray-100 p-5 rounded-lg shadow-sm mb-10"
      >
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input
            type="text"
            placeholder="Full Name"
            value={formData.name}
            required
            className="p-2 border rounded"
            onChange={(e) =>
              setFormData({ ...formData, name: e.target.value })
            }
          />
          <input
            type="text"
            placeholder="Position"
            value={formData.position}
            required
            className="p-2 border rounded"
            onChange={(e) =>
              setFormData({ ...formData, position: e.target.value })
            }
          />
          <input
            type="email"
            placeholder="Email"
            value={formData.email}
            required
            className="p-2 border rounded"
            onChange={(e) =>
              setFormData({ ...formData, email: e.target.value })
            }
          />
          <input
            type="file"
            accept="image/*"
            required
            className="p-2 border rounded"
            onChange={(e) => {
              const file = e.target.files[0];
              setFormData({ ...formData, image: file });
              setImagePreview(URL.createObjectURL(file));
            }}
          />
        </div>
        {imagePreview && (
          <img
            src={imagePreview}
            alt="Preview"
            className="mt-3 h-32 rounded border"
          />
        )}
        <textarea
          placeholder="Description (optional)"
          value={formData.description}
          className="p-2 border rounded w-full mt-4"
          onChange={(e) =>
            setFormData({ ...formData, description: e.target.value })
          }
        />
        <button
          type="submit"
          disabled={loading}
          className="mt-4 px-5 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded flex items-center gap-2 disabled:opacity-60"
        >
          <FaPlusCircle /> {loading ? 'Adding...' : 'Add Trusted Person'}
        </button>
      </form>

      {/* Cards */}
      <div className="grid gap-6 md:grid-cols-2 xl:grid-cols-3">
        {trustedPersons.map((person) => (
          <div
            key={person._id}
            className="bg-white p-4 rounded-lg border hover:shadow-lg transition duration-300 flex gap-4 items-start"
          >
            <div className="w-[130px] h-[90px] border rounded overflow-hidden">
              <img
                src={`${API.defaults.baseURL}${person.image}`}
                alt={person.name}
                className="w-full h-full object-cover"
              />
            </div>

            <div className="flex-1">
              <h3 className="text-lg font-semibold flex items-center gap-2">
                <FaUser className="text-gray-500" /> {person.name}
              </h3>
              <p className="text-sm text-gray-700 flex items-center gap-1">
                <FaBriefcase /> {person.position}
              </p>
              <p className="text-sm text-gray-600 flex items-center gap-1">
                <FaEnvelope /> {person.email}
              </p>
              {person.description && (
                <p className="text-xs text-gray-500 mt-1 flex items-center gap-1">
                  <FaRegFileAlt /> {person.description}
                </p>
              )}
            </div>

            <button
              onClick={() => handleDelete(person._id)}
              className="text-red-500 hover:text-red-700"
              title="Delete"
            >
              <FaTrash />
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminTrusted;
