import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UploadResource = () => {
  const [universities, setUniversities] = useState([]);
  const [formData, setFormData] = useState({
    university: '',
    department: '',
    course: '',
    semester: '',
    type: 'notes',
    title: '',
    file: null,
  });

  useEffect(() => {
    const fetchUniversities = async () => {
      try {
        const res = await axios.get('/api/universities');
        setUniversities(res.data);
      } catch (error) {
        console.error('Error fetching universities:', error);
      }
    };

    fetchUniversities();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, file: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const data = new FormData();
      for (let key in formData) {
        data.append(key, formData[key]);
      }

      await axios.post('/api/resources/upload', data);
      alert('✅ Resource uploaded successfully!');
    } catch (err) {
      console.error('Upload failed:', err);
      alert('❌ Failed to upload resource.');
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white shadow-md rounded-lg mt-8">
      <h2 className="text-2xl font-bold text-center mb-6 text-blue-600">Upload Academic Resource</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data" className="space-y-4">
        {/* University Dropdown */}
        <div>
          <label className="block font-medium mb-1">University</label>
          <select
            name="university"
            value={formData.university}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded"
            required
          >
            <option value="">Select University</option>
            {universities.map((uni) => (
              <option key={uni._id} value={uni._id}>{uni.name}</option>
            ))}
          </select>
        </div>

        {/* Department */}
        <div>
          <label className="block font-medium mb-1">Department</label>
          <input
            type="text"
            name="department"
            value={formData.department}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded"
            placeholder="e.g. Computer Science"
            required
          />
        </div>

        {/* Course */}
        <div>
          <label className="block font-medium mb-1">Course</label>
          <input
            type="text"
            name="course"
            value={formData.course}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded"
            placeholder="e.g. Data Structures"
            required
          />
        </div>

        {/* Semester */}
        <div>
          <label className="block font-medium mb-1">Semester</label>
          <input
            type="text"
            name="semester"
            value={formData.semester}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded"
            placeholder="e.g. 4th"
            required
          />
        </div>

        {/* Title */}
        <div>
          <label className="block font-medium mb-1">Title</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded"
            placeholder="Title of the document"
            required
          />
        </div>

        {/* Type */}
        <div>
          <label className="block font-medium mb-1">Type</label>
          <select
            name="type"
            value={formData.type}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded"
            required
          >
            <option value="notes">Notes</option>
            <option value="syllabus">Syllabus</option>
            <option value="papers">Papers</option>
          </select>
        </div>

        {/* File Upload */}
        <div>
          <label className="block font-medium mb-1">Upload File</label>
          <input
            type="file"
            accept=".pdf,.doc,.docx,.ppt,.pptx,.jpg,.jpeg,.png"
            onChange={handleFileChange}
            className="w-full border border-gray-300 p-2 rounded bg-white"
            required
          />
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700"
        >
          Upload Resource
        </button>
      </form>
    </div>
  );
};

export default UploadResource;
