import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddCourses = () => {
  const [formData, setFormData] = useState({
    courseName: '',
    department: '',
    semester: '',
    university: '',
    type: ''
  });

  const [universities, setUniversities] = useState([]);

  // Fetch universities on component mount
  useEffect(() => {
    axios.get('http://localhost:5000/api/universities')
      .then(res => setUniversities(res.data))
      .catch(err => console.error('Error loading universities:', err));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { university, courseName, semester, department, type } = formData;

    if (!university || !courseName || !semester || !department || !type) {
      alert('⚠️ Please fill all fields.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/courses', formData);
      alert('✅ Course added successfully!');
      console.log('Course added:', response.data);

      // Reset form
      setFormData({
        courseName: '',
        department: '',
        semester: '',
        university: '',
        type: ''
      });
    } catch (err) {
      console.error('Add course error:', err.response?.data || err.message);
      alert('❌ Failed to add course.');
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-10 bg-white dark:bg-gray-800 text-gray-900 dark:text-white p-6 rounded-xl shadow-md">
      <h2 className="text-3xl font-bold text-green-700 dark:text-green-300 mb-6">➕ Add a New Course</h2>

      <form onSubmit={handleSubmit} className="space-y-5">

        <div>
          <label className="block mb-1 font-medium">Course Name</label>
          <input
            type="text"
            name="courseName"
            placeholder="e.g. B.Tech CSE"
            value={formData.courseName}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2 focus:outline-none focus:ring focus:ring-blue-300"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-medium">Department</label>
          <input
            type="text"
            name="department"
            placeholder="e.g. Computer Science"
            value={formData.department}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2 focus:outline-none focus:ring focus:ring-blue-300"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-medium">Semester</label>
          <input
            type="number"
            name="semester"
            placeholder="e.g. 1"
            value={formData.semester}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2 focus:outline-none focus:ring focus:ring-blue-300"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-medium">Course Type</label>
          <select
            name="type"
            value={formData.type}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2 focus:outline-none focus:ring focus:ring-blue-300"
            required
          >
            <option value="">-- Select Course Type --</option>
            <option value="UG">Undergraduate (UG)</option>
            <option value="PG">Postgraduate (PG)</option>
            <option value="PhD">Doctoral (PhD)</option>
          </select>
        </div>

        <div>
          <label className="block mb-1 font-medium">University</label>
          <select
            name="university"
            value={formData.university}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2 focus:outline-none focus:ring focus:ring-blue-300"
            required
          >
            <option value="">-- Select University --</option>
            {universities.map((uni) => (
              <option key={uni._id} value={uni._id}>
                {uni.name}
              </option>
            ))}
          </select>
        </div>

        <button
          type="submit"
          className="bg-green-600 hover:bg-green-700 text-white font-medium px-6 py-2 rounded transition"
        >
          Submit Course
        </button>
      </form>
    </div>
  );
};

export default AddCourses;
