import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { FaUniversity, FaGlobe } from 'react-icons/fa';

const Universities = () => {
  const [universities, setUniversities] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/universities')
      .then(res => setUniversities(res.data))
      .catch(err => console.error('Failed to fetch universities:', err));
  }, []);

  return (
    <div className="min-h-screen px-6 py-16 bg-gray-50 dark:bg-gray-900 text-gray-800 dark:text-white">
      <h1 className="text-4xl sm:text-5xl font-extrabold text-center mb-12 text-blue-800 dark:text-blue-300 flex justify-center items-center gap-3">
        <FaUniversity className="text-yellow-400 text-5xl" />
        <span className="tracking-wide">Registered Universities</span>
      </h1>

      <div className="grid sm:grid-cols-2 md:grid-cols-3 gap-8 max-w-6xl mx-auto">
        {universities.map((uni) => (
          <div
            key={uni._id}
            className="bg-white dark:bg-gray-800 shadow-md rounded-xl p-6 border border-blue-100 dark:border-gray-700 flex flex-col justify-between"
          >
            <div>
              {/* Logo */}
              {uni.logo && (
                <img
                  src={uni.logo}
                  alt={`${uni.name} Logo`}
                  className="w-20 h-20 object-contain mb-4"
                />
              )}

              <h2 className="text-2xl font-semibold text-blue-700 dark:text-blue-200 mb-2">{uni.name}</h2>
              <p className="text-sm text-gray-600 dark:text-gray-400 mb-1">
                <strong>Short Name:</strong> {uni.shortName || 'N/A'}
              </p>
              <p className="text-sm text-gray-600 dark:text-gray-400 mb-1">
                <strong>Location:</strong> {uni.location || 'N/A'}
              </p>
              <p className="text-sm text-gray-600 dark:text-gray-400 mb-1">
                <strong>Departments:</strong> {uni.departments?.length || 0}
              </p>

              {/* Website link */}
              {uni.website && (
                <a
                  href={uni.website}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="inline-flex items-center gap-2 text-blue-600 hover:underline text-sm mt-1"
                >
                  <FaGlobe /> Visit Website
                </a>
              )}
            </div>

            <Link to={`/coursepages/${uni._id}/courses`}>
              <button className="mt-6 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 w-full">
                Explore Courses
              </button>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Universities;
